import React from 'react'
import validValue from '../Service/ValidValue';
const validForm = (form) => {
    const array = [];
    for (let x in form) {
        array.push({ id: x, config: form[x] })
    }
    array.forEach(element => {
        const name=element.id;
        if (element.config.validition["required"] === true) {
            if (element.config.value === "") {
                return "this " + name + " is requried";
            }
        }
        else
            return validValue(element.config.value, element.config.validition);
    });
}
export default validForm;
