import React, { Component } from 'react'
import { Button, Form, Grid, Header, Image, Loader, Dimmer, Segment, Message, Item, Label, Icon } from 'semantic-ui-react';
import "semantic-ui-css"
import axios from 'axios';
import { getEnterpriseById } from "../../../Store/actions/EnterpriseAction";
import { getCards } from "../../../Store/actions/EnterpriseAction";
import { connect } from 'react-redux';
import "../../Public.css"
class EnterpCards extends Component {
    state = {
        loading: false,
        myCards: []
    }
    componentDidMount() {
        this.props.selectCards(this.props.enterprise.C_id);
    }
    render() {
        console.log(this.props.cards)
        return (
            <div style={{ textAlign: "center" }}>

               {!this.props.loading?<div><h1 style={{ fontSize: '20px', textAlign: "center", color: '#00b5ad' }}>| My Card/s |</h1>
                <Button.Group vertical>
                    <Button disabled>Events</Button>
                    <Button>Photos</Button>
                </Button.Group>
)
                <Button style={{ marginLeft: "30vw", width: "70vw", height: "6vh" }} label="EXTRA CARD" labelPosition="left" content="Watch AND Update >>"></Button>
                <Button type="submit" color='teal' style={{ fontSize: "18px", marginTop: "9vh", padding: "2vh", width: "25%", backgroundColor: "#282c34" }}>New Card</Button></div>
:""}
            </div>
        )
    }
}
const mapStateToProps = state => {
    return {
        enterprise: state.enterprise.enterprise,
        cards: state.enterprise.cards,
        loading:state.enterprise.loading,
    }
}
const mapDispatchToProps = dispatch => {
    return {
        selectCards:(id)=>dispatch(getCards(id)),
    }
}
export default connect(mapStateToProps, mapDispatchToProps)(EnterpCards);
