import React, { Component } from 'react';
import { Button, Form, Grid, Header, Image, Message, Segment } from 'semantic-ui-react';
import 'semantic-ui-css/semantic.min.css';
import { NavLink, withRouter, useLocation, Prompt } from 'react-router-dom';
import axios from 'axios';
import { connect } from 'react-redux'
class Login extends Component {
  state = {
    mail: '',
    password: ''
  }
  getUser = () => {
    axios.get(`http://localhost:64430/api/users?password=${this.state.password}&mail=${this.state.mail}`).then(x => {
      this.props.selectUser(x.data)
      this.props.history.replace('/users/cards');
      console.log(this.props.user);
    }
    ).catch(x => { document.getElementById("login_message").innerHTML=x.response.data.Message; }
    );
  }
  getEnterprise = () => {
    axios.get(`http://localhost:64430/api/SendMail?password=${this.state.password}&mail=${this.state.mail}`).then(x => {
      this.props.selectEnterprise(x.data);
      console.log(this.props.enterprise);
      // this.sendMail();
    }
    ).catch(x => { console.log(x.data); }
    );
  }
  // sendMail = () => {
  //   document.getElementById("email_message").style.cssText = 'display:inline-block';
  //   console.log(this.props.enterprise);
  //   axios.get(`http://localhost:64430/api/Email?id=${this.props.enterprise.C_id}&mail=${this.props.enterprise.Email}`).then(x => {
  //     console.log(x.data)
  //   }).catch(x => { console.log(x.response.data.Message) })
  // }
  newPassword = () => {
    if (this.props.location.pathname === '/users/login')
    {

    
    const status=true;
      axios.get(`http://localhost:64430/api/Users?email=${this.state.mail}`).then(x => {
        console.log(x.data);
         this.props.selectUser(x.data)
        console.log(this.props.user);
        axios.get(`http://localhost:64430/api/Email?email=${this.props.user.Email} &status=${status}`).then(x => {
          // console.log("yes")
        }).catch(x => console.log("not send"))
        document.getElementById("newPassword").innerHTML = "sending email to:  " + x.data.Email;
      }).catch(x => document.getElementById("newPassword").innerHTML = x.response.data.Message);
    }
      else{
    const status=false;
      axios.get(`http://localhost:64430/api/Enterprises?email=${this.state.mail} `).then(x => {
        console.log(x.data);
        this.props.selectEnterprise(x.data);
        axios.get(`http://localhost:64430/api/Email?email=${this.props.enterprise.Email} &status=${status}`).then(x => {

        }).catch(x=>console.log("fsaf"))
        document.getElementById("newPassword").innerHTML =  "sending email to:  "+x.data.Email;
      }).catch(x => document.getElementById("newPassword").innerHTML = x.response.data.Message);
  }}
  render() {
    return (
      <Grid textAlign='center' style={{ height: '100vh' }} verticalAlign='middle'>
        <Grid.Column style={{ maxWidth: 450 }}>
          <Header as='h2' color='teal' textAlign='center'>
            <Image src='/Images/logo.png' /> Log-in to your account
        </Header>
          <Form size='tiny' onSubmit={this.props.location.pathname === "/enterprises/login" ? (this.getEnterprise) : (this.getUser)}>
            <Segment stacked>
              <Form.Input className="mail"
                fluid icon='user'
                iconPosition='left'
                placeholder='E-mail address'
                required
                // error
                type='email'
                onChange={(event) => this.setState({ mail: event.target.value })}
              />
              <Form.Input className="password"
                fluid
                icon='lock'
                iconPosition='left'
                placeholder='Password'
                type='password'
                required
                onChange={(event) => this.setState({ password: event.target.value })}
              // className={!value.valid?'error':null}
              />
              <div id="login_message" style={{color:"red"}}></div>
              <Button type="submit" color='teal' fluid size='large'  >
                Login
            </Button>
              <div id="email_message" style={{ display: 'none' }}>sending email</div>
            </Segment>
          </Form>
          <Message>
            New to us?<NavLink to={this.props.location.pathname === '/users/login' ? '/users/signUp' : '/enterprises/signUp'} >Sign Up</NavLink>&nbsp;
      </Message>
          <Message>
            <label>forget your password?<label onClick={this.newPassword} style={{ color: 'blue' }}>new password</label></label>
          </Message>
          <div id="newPassword"></div>

        </Grid.Column>
      </Grid>
    )
  }
}
const mapStateToProps = state => {
  return {
    user: state.user.user,
    enterprise: state.enterprise.enterprise
  }
}
const mapDispatchToProps = dispatch => {
  return {
    selectUser: (user) => dispatch({ type: "SELECT_USER", payload: user }),
    selectEnterprise: (enterprise) => dispatch({ type: "SELECT_ENTERPRISE", payload: enterprise }),
  }
}
export default connect(mapStateToProps, mapDispatchToProps)(Login);
