import React, { Component, createRef } from "react";
import { NavLink } from 'react-router-dom';
import { connect } from 'react-redux'
import {
    Icon,
    Button,
    Menu,
    Sidebar, Sticky, Ref
} from "semantic-ui-react";
import { white } from "color-name";
class MySidebar extends Component {
    navlinks = [{ value: "Home", path: "home" },
    { value: "About", path: "about" },
    { value: "Why it's worth me", path: "/" },
    { value: "Why it's worth my enterprise", path: "/" },
    { value: "Enterprises", path: "stores" },
    { value: "Contact us", path: "contact" }]
//     if(this.props.user!== null) {
//     // list = [{ value: "Home", path: "home" },
//     // { value: "About", path: "about" },
//     // { value: "Why it's worth me", path: "/" },
//     // { value: "Why it's worth my enterprise", path: "/" },
//     // { value: "Enterprises", path: "stores" },
//     // { value: "Contact us", path: "contact" }]
// }
//     else {

// }
// if(enterprise)
// state = {
//     visible: false,
// };
handleHideClick = () => this.setState({ visible: false });
handleShowClick = () => this.setState({ visible: true });
handleSidebarHide = () => this.setState({ visible: false });

render() {
    const contextRef = createRef();
    // const array = [];
    // for (let x in this.state.navlinks) {
    //     array.push({ id: x, config: this.state.navlinks[x] })
    // }
    const { visible } = this.state;
    return (
        <div>
            <Ref innerRef={contextRef}>
                <Sidebar.Pushable style={{ transform: "none" }}>
                    <Sticky>
                        <div style={{ backgroundColor: "#282c34", textAlign: "left", zIndex: "50", height: "7vh" }} >
                            <Button id="sidebarout" onClick={this.handleShowClick}>|||</Button>
                            <p style={{ display: "inline-block", color: "white", fontSize: "12px", marginLeft: "2vw" }}><Icon name="phone"></Icon>CONTACT US : 052-7689412</p>
                        </div>
                        <Sidebar style={{ backgroundColor: "#282c34" }}
                            as={Menu}
                            animation="overlay"
                            icon="labeled"
                            inverted
                            onHide={this.handleSidebarHide}
                            vertical
                            visible={false}
                            width="thin" >
                            <Button id="sidebarin" onClick={this.handleHideClick}>X</Button>
                            {this.navlinks.map((x, i) => {
                                return <div>
                                    {false ?
                                        <Menu.Item as='a' href={"#" + x.path}>{x.value}</Menu.Item> :
                                        <Menu.Item as="a"><NavLink to={x.path}>{x.value}</NavLink></Menu.Item>
                                    }
                                </div>
                            })}
                        </Sidebar>
                    </Sticky>
                </Sidebar.Pushable>
            </Ref>
        </div>

    );
}
}
const mapStateToProps = state => {
    return {
        user: state.user.user,
        enterprise: state.enterprise.enterprise,
    }
}
export default connect(mapStateToProps, null)(MySidebar);
