import React, { Component } from 'react';
import axios from 'axios';
import { NavLink } from 'react-router-dom';
import { getAllCards } from "../../../Store/actions/EnterpriseAction";
import { connect } from 'react-redux';
import { Input, Icon, Dimmer, Loader, Transition, Button, Segment, Card, Image } from 'semantic-ui-react';
import ReactCardFlip from 'react-card-flip';
import { isThisMonth } from 'date-fns';
class NewCard extends Component {
    state = {
        loading: false,
        stores: [],
        isFlipped: false,
        index: -1,
        value: '',
        data: [],
    }
    componentDidMount() {
         this.props.selectAllCards();
         this.setState({data:this.props.allCards})
    }
    componentDidUpdate() {
        if (this.state.data.length === 0 && this.props.allCards.length > 0) {
            this.setState({ data: this.props.allCards })
        }
    }
    onclick = (e, card) => {
        this.setState({ loading: true });
        var today = new Date();
        var date = today.getFullYear() + '-' + (today.getMonth() + 1) + '-' + today.getDate();
        console.log(this.props.user)
        const data = {};
        data['UserId'] = this.props.user['C_id']
        data['EnterpCardId'] = card.CardId;
        data['BuyDate'] = date;
        data['ExpireDate'] = today.getFullYear() + card['Duration'] + '-' + (today.getMonth() + 1) + '-' + today.getDate();
        console.log(data)
        axios.post(`http://localhost:64430/api/ClubCards`, data
        ).then(x => {
            this.props.history.replace('/users/clearing')
        }
        ).catch(x => {
            this.setState({ loading: false, errorMessage: x.response.data.Message });
        });
    }
    search = () => {
        this.setState({ loading: true });
        const data = [];
        this.props.allCards.map((x, i) => {
            if (x.Name == this.state.value)
                data[i] = x;
            else
                console.log(x)
            console.log(data)

            this.setState({ data: data, value: '' })
            // alert(x)
        })
        // alert(x)

        this.setState({ loading: false });
    }
    render() {
        return (
            <div >
                <h1 style={{ color: '#00b5ad', textAlign: "center", fontSize: '20px', border: 'none' }}>| New Card |</h1>
                <div style={{ width: "100%" }}><Input placeholder='Search...' style={{ marginLeft: '10VW' }} value={this.state.value} onChange={e => this.setState({ value: e.target.value })} /><Button icon='search' onClick={this.search}></Button></div>
                <Button style={{marginLeft:'93%'}} onClick={e=>this.setState({data:this.props.allCards})}>all cards</Button>
                <div style={{ marginLeft: "8vw" }}>
                    {this.state.data!== null ?
                        this.state.data.map((x, i) => {

                            return <div class="flip-box"  >
                                <div class="flip-box-inner" style={{ width: "12vw", height: "15vh", marginLeft: "2vw", marginTop: "10vh" }} onClick={e => this.onclick(e, x)}>
                                    <div class="flip-box-front" style={{ backgroundImage: `url(${x.Img})`, backgroundSize: "100%", backgroundRepeat: "no-repeat", width: "100%", height: "100%", borderRadius: "20px", boxShadow: " 5px 10px 18px #888888" }}>
                                        {/* <Image src={x.Img} style={{width:"60%",height:"60%"}}></Image> */}
                                    </div>
                                    <div class="flip-box-back" style={{ backgroundColor: "rgb(40, 44, 52)", color: "white", width: "100%", height: "100%", borderRadius: "20px", boxShadow: " 5px 10px 18px #888888" }}>
                                        <h2>{x.Name}</h2>
                                        <div style={{ backgroundColor: "white", width: "100%", height: "1vh", marginTop: '0.5vh' }}></div>
                                        <p>{x.Type}</p>
                                        <p>{x.Cost + "$"}</p>
                                    </div>
                                </div>
                            </div>
                        })
                        : "No Results"
                    }
                </div>
                <Dimmer active={this.state.loading} inverted>
                    <Loader size='large'>Loading</Loader>
                </Dimmer>
            </div>
        )
    }
}
const mapStateToProps = state => {
    return {
        allCards: state.enterprise.allCards,
        user: state.user.user,

    }
}
const mapDispatchToProps = dispatch => {
    return {
        selectAllCards: () => dispatch(getAllCards()),
    }
}
export default connect(mapStateToProps, mapDispatchToProps)(NewCard);
