import React, { Component } from "react";
import {
    Button,
    Header,
    Icon,
    Image,
    Menu,
    Segment,
    Sidebar
} from "semantic-ui-react";
import Home from "../Home/Home";

class MySidebar extends Component {
    state = { visible: false };

    handleSidebarHide = () => this.setState({ visible: false });
    handleSidebarToggle = () =>
        this.setState(prevState => ({ visible: !prevState.visible }));

    render() {
        const { visible } = this.state;
        return (
            <div>
                <Sidebar.Pushable  as={Segment.Group} raised >
                    <Sidebar style={{backgroundColor:"transparent"}}
                        as={Menu}
                        animation='overlay'
                        icon='labeled'
                        inverted
                        // onHide={() => this.handleSidebarHide}
                        vertical
                        visible={visible}
                        width='thin'
                    >
                        <div id="sidebarin" onClick={this.handleSidebarHide}>X</div>
                        <Menu.Item style={{backgroundColor:"gray"}} as='a'>Home</Menu.Item>
                        <Menu.Item style={{backgroundColor:"gray"}} as='a'>Games</Menu.Item>
                        <Menu.Item style={{backgroundColor:"gray"}} as='a'>Channels</Menu.Item>
                    </Sidebar>
                    {!this.state.visible ? <Button id="sidebarout" onClick={this.handleSidebarToggle} style={{ position:"absolute"}}>|||</Button> : ""}
                    <Home></Home>
                </Sidebar.Pushable>
            </div>
        );
    }
}
export default MySidebar
