import validator from 'validator';
const newPassword = (form) => {
    const mail = { ...form["mail"] }
    mail.newPassword = true;
    console.log(mail.newPassword)
    form["mail"]=mail;
    var obj=null;
    if (form["mail"].newPassword === true) {
        if (form["mail"].value === "") {
            obj = { id: "mail", error: form["mail"].text+" is requried" };
        }
        else
            if (!validator.isEmail(form["mail"].value))
                obj = { id: "mail", error: form["mail"].text+" is not valid" };
    }
    return obj;
}
export default newPassword;
