import React, { Component } from 'react'
import { Menu,Segment } from "semantic-ui-react"
import { NavLink } from 'react-router-dom';
import { connect } from 'react-redux';
class Header extends Component {
    navlinks = [{ value: "Home", path: "/" }];
    state = { activeItem: 'home' }

    handleItemClick = (e, { name }) => this.setState({ activeItem: name })
    componentDidUpdate() {
        console.log("update", this.props.user)
        if (this.props.user !== null)
            this.navlinks = [
                { value: "My Cards", path: "/users/cards" },
                { value: "New Cards", path: "/users/new" },
                { value: "Update Profile", path: "/users/ProfileUpdate" },
                { value: "Discontact", path: "" }
            ]
        else
            if (this.props.enterprise !== null) {
                this.navlinks = [
                    { value: "My Cards", path: "/enterprises/enterpCards" },
                    { value: "Update Profile", path: "/enterprises/ProfileUpdate" },
                    { value: "New Card", path: "/enterprises/newCard" },
                    { value: "Agreement and price list", path: "/agreement" },
                    { value: "Discontact", path: "" }]
            }
            else
                this.navlinks = [{ value: "Home", path: "/" }]
    }
    render() {
        const { activeItem } = this.state
        return (
            <div style={{ position: "fixed", width: "100vw", zIndex: "300" }}>
                <Segment inverted>
                    <Menu inverted pointing secondary style={{ backgroundColor: "#191a19" }}>
                        {this.navlinks.map((x, i) => {
                            return <NavLink to={x.path}><Menu.Item
                                name={x.value}
                                active={activeItem === 'home'}
                                onClick={this.handleItemClick}
                                as='a' key={i}>

                            </Menu.Item> </NavLink>
                        })}
                    </Menu></Segment>
            </div>
        );
    }
}
const mapStateToProps = state => {
    return {
        user: state.user.user,
        enterprise: state.enterprise.enterprise,
    }
}
export default connect(mapStateToProps, null)(Header);
