import React, { Component } from 'react';
import ReactCardFlip from 'react-card-flip';
import { Button, Segment, Card, Image } from 'semantic-ui-react';
class CardClip extends Component {
    state = {
        isFlipped: false
    };
    handleClick = (e) => {
        e.preventDefault();
        this.setState({ isFlipped: !this.state.isFlipped });
    }
    render() {
        return (
            <ReactCardFlip isFlipped={this.state.isFlipped} flipDirection="vertical">
                <Card onMouseOver={this.handleClick} style={{ height: "12vh",backgroundImage:'url(https://react.semantic-ui.com/images/avatar/large/matthew.png)' }}>
                Matthew is a pianist living in Nashville.
                </Card>
                <Card onMouseOut={this.handleClick} style={{ height: "12vh" }}>
                            Matthew is a pianist living in Nashville.
                </Card>
            </ReactCardFlip>
        )
    }
}
export default CardClip;
