import React, { Component } from 'react';
import 'semantic-ui-css/semantic.min.css';
import { connect } from 'react-redux'
import { Input, Form, Dropdown, Grid, Popup, Button } from 'semantic-ui-react';
class Lotery extends Component {
    state = {
        LoteryTypes: [
            { key: 'Discount', text: 'Discount', value: 'Discount' },
            { key: "Plus Free", text: "Plus Free", value: "Plus Free" },
            { key: "X in Y", text: "X in Y", value: "X in Y" }],
        SumTypes: [
            { key: '%', text: '%', value: '%' },
            { key: '$', text: '$', value: '$' }
        ]
    }
    OnChangeSelect = (event, { value }) => {
        const form = { ...this.state.form }
        if (value.length > 1) {
            for (let x in form) {
                form[x] = "";
            }
            form.LoteryType = value;
            form.SumType = "$"
        }
        else
            form.SumType = value;
        this.setState({ form: form });
        this.props.changed(this.props.index, form)
    }
    onChange = (value, id) => {
        const form = { ...this.state.form }
        form[id] = value;
        this.setState({ form: form });
        this.props.changed(this.props.index, form)

    }
    render() {
        return (
            <div style={{ textAlign: "left" }}>
                <Form style={{ marginLeft: "9%" }}>
                    <Grid >
                        <Grid.Column style={{ width: 950 }}>
                            <Grid.Row>
                                <Form.Group widths="equal">
                                    <Form.Select label="Lottery Types" options={this.state.LoteryTypes}
                                        defaultValue={this.state.form.LoteryType}
                                        onChange={this.OnChangeSelect}></Form.Select>
                                    {this.state.form.LoteryType === "Discount" ?
                                        <Popup
                                            content="for example:30% or 30$"
                                            trigger={<Form.Input
                                                onChange={(e) => this.onChange(e.target.value, "Sum")}
                                                value={this.state.form.Sum}
                                                min={1}
                                                label="Details"
                                                type="number"
                                                labelPosition='right'
                                                action={
                                                    <Dropdown id="sumType" button basic floating options={this.state.SumTypes} defaultValue={this.state.form.SumType} onChange={this.OnChangeSelect}
                                                    />}></Form.Input>} /> : this.state.form.LoteryType === "Plus Free" ?
                                            <Form.Input label="Details">
                                                <Popup content="for example:1+1" trigger={<Input type="number" min={1} max={10} value={this.state.form.Num1} onChange={(e) => this.onChange(e.target.value, "Num1")}></Input>} />
                                                <Button style={{ borderRadius: "100px" }} onClick={event => event.preventDefault()}>+</Button>
                                                <Popup content="for example:1+1" trigger={<Input type="number" min={1} max={10} value={this.state.form.Num2} onChange={(e) => this.onChange(e.target.value, "Num2")}></Input>} /></Form.Input> :
                                            <Form.Input label="Details">
                                                <Popup content="for example:3 In 100$" trigger={<Input type="number" min={1} max={10} value={this.state.form.Num1} onChange={(e) => this.onChange(e.target.value, "Num1")}></Input>} />
                                                <Button style={{ borderRadius: "100px" }} onClick={event => event.preventDefault()}>In</Button>
                                                <Popup content="for example:3 In 100$" trigger={<Input type="number" min={1} max={1000000} value={this.state.form.Num2} onChange={(e) => this.onChange(e.target.value, "Num2")}></Input>} /></Form.Input>}
                                    <Form.Input type="date" label="Expire Date" value={this.state.form.ExpireDate} onChange={(e) => this.onChange(e.target.value, "ExpireDate")}>
                                    </Form.Input>
                                </Form.Group>
                            </Grid.Row>
                            <Grid.Row>
                                <Form.TextArea label="Addition" type="text" placeholder="Addition" maxLength={50} value={this.state.form.Addition} onChange={(e) => this.onChange(e.target.value, "Addition")}></Form.TextArea></Grid.Row></Grid.Column></Grid>
                </Form>
            </div >
        )
    }
}
const mapStateToProps = state => {
    return {
        lottery: state.enterprise.lottery,
    }
}
const mapDispatchToProps = dispatch => {
    return {
        selectLottery: (lottery) => dispatch({ type: "SELECT_LOTTERY", payload: lottery }),
    }
}
export default connect(mapStateToProps, mapDispatchToProps)(Lotery);
