import React, { Component } from 'react'
import axios from 'axios';
import { connect } from 'react-redux'
import Card from './Card';
class Cards extends Component {
    state = {
        cards: [],
        loading: true
    }
    componentDidMount() {
        this.setState({ loading: true });
        console.log(this.props.user.C_id);
        axios.get(`http://localhost:64430/api/ClubCards?userId=${this.props.user.C_id}`).then(res => {
            this.setState({ cards: res.data })
            console.log(this.state.cards);
            this.setState({ loading: false });

        }).catch(x => { console.log("error") })
    }
    render() {
        let card = <div>loading...</div>;
        if (this.state.loading === false) {
            console.log(this.state.cards);
       card=this.state.cards.map((x, i) => {
                return <Card name={x.Name}
                key={i}
                    expire={x.ExpireDate}
                    src={x.Img}
                    type={x.Type}
                    points={x.Points}></Card>
            })
        }
        return (
            <div>
                <h1>{card}</h1>

            </div>
        )
    }
}
const mapStateToProps = state => {
    return {
        user: state.user.user
    }
}
export default connect(mapStateToProps, null)(Cards);
