import React, { Component } from 'react'
import axios from 'axios';
import Store from './MyStore';
import clickNext from "../Service/ClickNext"
import clickPrev from "../Service/ClickPrev"
class MyStores extends Component {
    state = {
        stores: [],
        selectedIndex: 0,
        // shown: [],
        // prevLength: "",
        // nextStopped: 0,
        // nextLength: "",

    }
    componentWillMount() {
        axios.get(`http://localhost:64430/api/Enterprises`).then(x => {
            this.setState({ stores: x.data, nextLength: x.data.length });
            // console.log(this.state.nextLength)
            // if (this.state.nextLength > 0) {
            //     var showIt = clickNext(this.state.nextLength, this.state.nextStopped, this.state.stores);
            //     this.setState({ shown: showIt });
            //     var nowStopped = (this.state.nextStopped + this.state.shown.length);
            //     this.setState({ nextStopped: nowStopped });
            //     var nowLength = (this.state.nextLength - this.state.nextStopped);
            //     this.setState({ nextLength: nowLength });
            // }
        });
    }
    handelClickNext = () => {
        if (this.state.selectedIndex == this.state.videos.length - 1)
            return;

        this.setState(prevState => ({
            selectedIndex: prevState.selectedIndex + 1
        }))
        // var showIt = clickNext(this.state.nextLength, this.state.nextStopped, this.state.stores);
        // this.setState({ shown: showIt });
        //     var nowStopped = (this.state.nextStopped + this.state.shown.length);
        //     this.setState({ nextStopped: nowStopped });
        //     var nowLength = (this.state.nextLength - this.state.nextStopped);
        //     this.setState({ nextLength: nowLength });
    }
    handelClickPrev = () => {
        this.setState({ prevLength: this.state.nextStopped });
        var showIt = clickPrev(this.state.prevLength, this.state.stores);
        this.setState({ shown: showIt });
        var nowLength = (this.state.prevLength - this.state.shown.length);
        this.setState({ prevLength: nowLength });
    }
    render() {
        console.log(this.state.nextLength, this.state.nextStopped)
        return (
            <div>
                <h3 style={{ backgroundColor: "#00b5ad", color: "white", textAlign: "left", paddingLeft: "1%" }}>Customers</h3>
                <button disabled={this.state.nextLength > 0 ? false : true} onClick={this.handelClickNext}>next</button>
                <button disabled={this.state.nextStopped > 3 ? false : true} onClick={this.handelClickPrev}>prev</button>
                <div>
                    {this.state.shown !== null ? this.state.shown.map((x, i) => {
                        return <Store name={x.Name} url={x.Url} key={i}></Store>
                    }) : <div>No Result</div>}
                </div>
            </div>
        )
    }
}
export default MyStores;
