import React, { Component } from 'react';
import { getEnterprise } from '../../Store/actions/EnterpriseAction'
import { getUser } from '../../Store/actions/UserAction'
import { Button, Form, Grid, Header, Image, Message, Segment, Loader, Dimmer } from 'semantic-ui-react';
import 'semantic-ui-css/semantic.min.css';
import { NavLink } from 'react-router-dom';
import axios from 'axios';
import { connect } from 'react-redux'
import onChangeInput from "../Service/onChangeInput";
import onSubmit from "../Service/onSubmit";
import newPassword from "../Service/newPassword"
class Login extends Component {
  state = {
    form: {
      mail: {
        newPassword: false,
        icon: "envelope",
        type: "text",
        value: "",
        text: "Email",
        validation: {
          pattern: /^[0-9a-zA-Z.+*%#!@]+$/,
          required: true,
          mail: true,
        }
      },
      password: {
        icon: "lock",
        type: "password",
        value: "",
        text: "Password",
        validation: {
          pattern: /^[0-9a-zA-Z]+$/,
          required: true,
          password: true,
        }
      }
    },
    trySend: "",
    loading: "",
    errorMessage: "",
    newPasswordMessage: "",
  }
  newPassword = () => {
    const form = { ...this.state.form };
    newPassword(form);
    const obj = onSubmit(form)
    if (obj !== null) {
      this.setState({ form: form, trySend: true, errorMessage: obj.error });
    }
    else {
      var route = "users";
      if (this.props.location.pathname !== '/users/login')
        route = "enterprises";
      // this.props.changeLoading(true);
      axios.get(`http://localhost:64430/api/${route}?email=${this.state.form.mail.value}`).then(x => {
        this.props.changeLoading(false);
        this.setState({ newPasswordMessage: "sending email to:  " + this.state.form.mail.value });
      }).catch(x => this.setState({ newPasswordMessage: x.response.data.Message }));
    }
  }
  handleSubmit = (e) => {
    const form = { ...this.state.form };
    const obj = onSubmit(form)
    if (obj !== null) {
      this.setState({ form: form, trySend: true, errorMessage: obj.error });
    }
    else {
      if (this.props.location.pathname === '/users/login') {
        this.props.selectUser(this.state.form.mail.value, this.state.form.password.value);
        this.props.changeLoading(false);
        this.props.history.replace('cards');
      }
      else
        this.props.selectEnterprise(this.state.mail, this.state.password);
    }
  }
  onChangeHendler = (e, id) => {
    this.setState({ trySend: false, errorMessage: "" })
    this.props.changeUserError();
    const form = { ...this.state.form };
    const inputChanged = { ...form[id] };
    onChangeInput(e, inputChanged, form, id);
    this.setState({ form: form })
  }
  render() {
    const array = [];
    for (let x in this.state.form) {
      array.push({ id: x, config: this.state.form[x] })
    }
    return (
      <Grid textAlign='center' style={{ height: '100vh' }} verticalAlign='middle'>
        <Grid.Column style={{ maxWidth: 450, boxShadow: '5px 2px 1px -1px rgba(0, 0, 0, 0.2), 5px 5px 5px 5px rgba(0, 0, 0, 0.14), 5px 1px 7px 4px rgba(0, 0, 0, 0.12)', borderRadius: '10%', height: '60%' }}>
          <Header as='h2' color='teal' textAlign='center'>
            Login to your accounts<br></br>
            <Image src='/Images/profile.PNG' style={{ height: '15vh', width: '8vw' }} />
          </Header>
          <Form size='tiny' onSubmit={this.handleSubmit}>
            <Segment stacked>
              {array.map((x, i) => {
                return <Form.Input
                  key={i} iconPosition="left"
                  type={x.config.type}
                  onChange={event => this.onChangeHendler(event, x.id)}
                  className={this.state.trySend && !this.state.form[x.id].valid ? "error" : x.id}
                  placeholder={x.config.text + "*"}
                  icon={x.config.icon}>
                </Form.Input>
              })}
              <div id="message" style={{ color: "red" }}>{this.state.errorMessage}</div>
              <Button type="submit" color='teal' fluid size='large'>
                Login
            </Button>
              <div id="email_message" style={{ color: "red", marginTop: "1vh" }}>{this.props.userError}</div>
            </Segment>
          </Form>
          <Message style={{ boxShadow: 'none' }}>
            New to us?<NavLink to={this.props.location.pathname === '/users/login' ? '/users/signUp' : '/enterprises/signUp'} style={{ color: '#00b5ad' }}>Sign Up</NavLink>&nbsp;
      </Message>
          <Message style={{ boxShadow: 'none' }}>
            <label >forget your password?<label onClick={this.newPassword} style={{ color: '#00b5ad' }}>new password</label></label>
            <div id="newPassword" style={{ color: "red" }}>{this.state.newPasswordMessage}</div>
          </Message> 
          <Dimmer id='dimmer' active={this.props.loading} inverted='false'>
          <Loader size='large'>Loading</Loader>
        </Dimmer> 
        </Grid.Column>
      
      </Grid>
    )
  }
}
const mapStateToProps = state => {
  return {
    user: state.user.user,
    enterprise: state.enterprise.enterprise,
    userError: state.user.error,
    loading: state.user.loading,
  }
}
const mapDispatchToProps = dispatch => {
  return {
    selectUser: (mail, password) => dispatch(getUser(mail, password)),
    selectEnterprise: (mail, password) => dispatch(getEnterprise(mail, password)),
    changeLoading: (status) => dispatch({ type: "LOADING", payload: status }),
    changeUserError: () => dispatch({ type: "ERROR", payload: "" })
  }
}
export default connect(mapStateToProps, mapDispatchToProps)(Login);
