import React, { Component } from 'react';
import {  Form, Grid, Header, Input, Popup, Divider } from 'semantic-ui-react';
import { InputFile } from 'semantic-ui-react-input-file';
import { connect } from 'react-redux'
import { selectCard } from "../../../Store/actions/EnterpriseAction"
import axios from 'axios';
class CardDetails extends Component {
    state = {
        form: {
            cost: {
                label: "Price For Customer",
                Icon: "$",
                type: 'number',
                step: 5,
                text: "Card Price *",
                value: ""
            },
            points: {
                label: "Count Points",
                Icon: " = 1 $",
                type: 'number',
                text: "Count Points *",
                value: ""
            },
            type: {
                options: [{ key: "Basic", text: 'Basic card', value: 'Basic card' }, { key: "Primium", text: 'Primium card', value: 'Primium card' }],
                label: "Card Type",
                text: "Type *",
                value: ""
            },
            img: {
                label: 'Enterprise Image',
                value: "",
                text: "select Img",
            }
        },
        fileName: "",
    }
    hanleSelectOnChange = (event, { value }) => {
        const form = { ...this.state.form }
        const input = { ...form["type"] }
        input.value = value;
        form["type"] = input
        this.setState({ form: form })
    }
    convertImg = (event) => {
        const file = event.target.files[0];
        if (file) {
            var name = file.name;
            const reader = new FileReader();
            reader.readAsDataURL(file);
            console.log(reader)
            reader.onload = () => {
                this.onChangeHandler(String(reader.result), "img")
                this.setState({ fileName: name })
            }
        }
    }
    onChangeHandler = (value, id) => {
        const form = { ...this.state.form }
        const input = { ...form[id] }
        input.value = value;
        form[id] = input
        this.setState({ form: form })
    }
    onSubmitHandler=()=>{
        for (let x in this.state.form) {
            array.push({ id: x, config: this.state.form[x] })}
    }
    render() {
        const form = this.state.form;
        const array = [];
        for (let x in this.state.form) {
            array.push({ id: x, config: this.state.form[x] })
        }
        return (<div style={{ marginLeft: "1vw" }}>
            {this.props.submit?this.onSubmitHandler:""}
            <h1 style={{ width: '30vw', marginLeft: '35vw', marginTop: '5vh', textAlign: 'center', fontSize: '180%', color: 'rgb(40, 44, 52)' }}>New Card</h1>
            <Header as='h2' color='teal' textAlign='left'>Card Details</Header>
            <Divider></Divider>
            <Form>
                <Grid><Grid.Column><Form.Group>
                    <Form.Input label={form.cost.label}>
                        <Input label={form.cost.icon} labelPosition="right" type={form.cost.type} onChange={event => this.onChangeHandler(event.target.value, "cost")} value={form.cost.value}
                            // // className={this.state.trySend && !this.state.form[x.id].valid ? "error" : x.id} */}
                            placeholder={form.cost.text}
                            step={form.cost.step}
                        /></Form.Input>
                    <Form.Input label={form.points.label}><Popup content="For example 5 point =1$" trigger={<Input onChange={event => this.onChangeHandler(event.target.value, "points")} value={form.points.value} type={form.points.type} label={form.points.icon} labelPosition="right" placeholder={form.points.text} />}
                    />
                    </Form.Input>
                    <Form.Select label={form.type.label}  placeholder={form.type.text} options={form.type.options}   onChange={this.hanleSelectOnChange} ></Form.Select>
                    <Form.Input label={form.img.label}>
                        <InputFile
                            button={{
                                labelPosition: "left",
                                label: <Input placeholder={form.img.text} value={this.state.fileName !== "" ? this.state.fileName : ""}></Input>
                            }}
                            input={{
                                onChange: this.convertImg,
                            }} /></Form.Input>
                </Form.Group> </Grid.Column> </Grid></Form>
        </div>
        )
    }
}
const mapStateToProps = state => {
    return {
     submit:state.enterprise.submit,
    }
  }
const mapDispatchToProps = dispatch => {
    return {
        selectCard: (card) => dispatch(selectCard(card)),
    }
}
export default connect(null, mapDispatchToProps)(CardDetails);
