import React, { Component } from 'react'
import { Button, Form, Grid, Header, Image, Loader, Dimmer, Segment } from 'semantic-ui-react';
import 'semantic-ui-css/semantic.min.css';
import axios from 'axios';
import { connect } from 'react-redux'
import onSubmit from "../Service/onSubmit";
import onChangeInput from "../Service/onChangeInput";
import Error from "../Error/Error";
class newPassword extends Component {
    state = {
        form: {
            password: {
                value: "",
                text: "Password",
                validation: {
                    pattern: /^[0-9a-zA-Z]+$/,
                    required: true,
                    password: true,
                }
            },
            confirm_password: {
                value: "",
                text: "Confirm Password",
                validation: {
                    pattern: /^[0-9a-zA-Z]+$/,
                    required: true,
                    confirm_password: true,
                }
            }
        },
        trySend: "",
        loading: "",
        errorMessage: "",
        permission: true,
        timeIsHoverError: "",
    }

    componentDidMount() {
        let search = this.props.location.search;
        let params = new URLSearchParams(search);
        let key = params.get('key');
        axios.get(`http://localhost:64430/api/SendMail?key=${key}`).then(x => {
            this.setState({ permission: true });
            // this.setState({ timeIsHoverError: x.response.Message, permission: false })
        }).catch(x => console.dir(x.response.data.Message));
    }
    onChangeHendler = (e, id) => {
        this.setState({ trySend: false })
        const form = { ...this.state.form };
        const inputChanged = { ...form[id] };
        onChangeInput(e, inputChanged, form, id);
        this.setState({ form: form })
    }
    changePassword = () => {
        const form = { ...this.state.form };
        const obj = onSubmit(form)
        if (obj !== null) {
            this.setState({ form: form, trySend: true, errorMessage: obj.error });
        }
        else {
            let search = this.props.location.search;
            let params = new URLSearchParams(search);
            let key = params.get('key');
            const location = "users";
            if (this.props.location.pathname != '/users/newpassword')
                location = "enterprises";
            axios.post(`http://localhost:64430/api/${location}?key=${key}&password=${this.state.password}`).then(x => {
                this.setState({ errorMessage: "Password was changed successfully" });
            }).catch(x => this.setState({ errorMessage: x.response.data.Message }));
        }
    }
    render() {
        const array = [];
        for (let x in this.state.form) {
            array.push({ id: x, config: this.state.form[x] })
        }
        return (<div>
            {!this.state.permission ? <Grid textAlign='center' style={{ height: '100vh' }} verticalAlign='middle'>
                <Grid.Column style={{ maxWidth: 450 }}>
                    <Header as='h2' color='teal' textAlign='center'>
                        <Image src='/Images/logo.png' /> New Password
              </Header>
                    <Form size='large' onSubmit={this.changePassword}>
                        <Segment stacked>
                            {array.map((x, i) => {
                                return <Form.Input
                                    key={i} iconPosition="left"
                                    type="password"
                                    onChange={event => this.onChangeHendler(event, x.id)}
                                    className={this.state.trySend && !this.state.form[x.id].valid ? "error" : x.id}
                                    placeholder={x.config.text + "*"}
                                    icon="lock">
                                </Form.Input>
                            })}
                            <div id="message" style={{ color: "red" }}>{this.state.errorMessage}</div>
                            <Button type="submit" color='teal' fluid size='large'>
                                Change Password
                        </Button>
                        </Segment>
                    </Form>
                </Grid.Column>
                <Dimmer active={this.state.loading} inverted>
                    <Loader size='large'>Loading</Loader>
                </Dimmer>
            </Grid> : <Error error={this.state.timeIsHoverError}></Error>}
            <script src="https://code.jquery.com/jquery-1.12.4.js" type="text/javascript"></script>

<script src="https://www.negishim.com/accessibility/accessibility_pro.js" type="text/javascript"></script>
<script type="text/javascript">
    accessibility_rtl = true;
    pixel_from_side = 20;
    pixel_from_start = 20;
</script>
        </div >
        )
    }
}
const mapStateToProps = state => {
    return {
        user: state.user.user,
        enterprise: state.enterprise.enterprise,
    }
}
const mapDispatchToProps = dispatch => {
    return {
        selectUser: (user) => dispatch({ type: "SELECT_USER", payload: user }),
        selectEnterprise: (enterprise) => dispatch({ type: "SELECT_ENTERPRISE", payload: enterprise }),
    }
}

export default connect(mapStateToProps, mapDispatchToProps)(newPassword);
