import React, { Component } from 'react'
import Lotery from './Lotery';
import "../../Public.css";
import 'semantic-ui-css/semantic.min.css';
import { connect } from 'react-redux'
import { Button, Form, Header, Divider } from 'semantic-ui-react';
import CardDetails from "./CardDetails"
import { submitNewCard } from '../../../Store/actionTypes';
import axios from 'axios';
class NewCard extends Component {
    state = {
        lotteries: {
            new: {
                header: "Lottery For Newers",
                active: false,
            },
            birthday: {
                header: "Lottery For Birthday",
                active: false,
            },
            club_members: {
                header: "Lottery For Club Members",
                active: false,
            }
        },
        lotery: [],
        card: {}
    }
    onClick = (event,x) => {
        event.preventDefault();
        const lotteries = { ...this.state.lotteries };
        const lotery = { ...lotteries[x.id] };
        lotery.active = !lotery.active;
        lotteries[x.id] = lotery;
        this.setState({ lotteries: lotteries })
    }
    changeLotery = (index, value) => {
        const lotery = [...this.state.lotery];
        lotery[index] = value;
        this.setState({ lotery: lotery })
    }
    changeCard = (card) => {
        this.setState({ card: card })
    }
    onSubmit = () => {
        const card = { ...this.state.card }
        card["EnterpId"] = this.props.enterprise.C_id;
        this.setState({ card: card })
        axios.post(`http://localhost:64430/api/EnterpCards`,{Card:this.state.card,Lotteries:this.state.lotery}).then(x => {
            // this.props.history.replace('/enterprises/login');
            console.log(x);
        }).catch(x => {
            // this.setState({ loading: false, errorMessage: x.response.data.Message });
        });
    }
    render() {
        const array = [];
        for (let x in this.state.lotteries) {
            array.push({ id: x, config: this.state.lotteries[x] })
        }
        console.log(this.state.lotery)
        return (
            <div>
                <CardDetails changed={this.changeCard}></CardDetails>
                <Header as='h2' color='teal' textAlign='left' style={{ marginLeft: "1vw" }}> Lotteries </Header>
                <Form onSubmit={this.onSubmit}>
                    {array.map((x, i) => {
                        return <div>
                            <Divider></Divider>
                            <Header as='h4' color='teal' textAlign='left'>
                                <Button style={{ marginRight: "3vw", marginLeft: "1vw" }} onClick={event => this.onClick(event,x)}>{x.config.active ? "Delete" : "Insert"}</Button>
                                {x.config.header}
                            </Header>
                            {x.config.active == true ? <Lotery index={i} changed={this.changeLotery}></Lotery> : ""}
                            {/* {x.id==="club_members"?<Button>Insert</Button>:""} */}
                        </div>
                    })}
                    <Button type="submit" color='teal' style={{ fontSize: "18px", marginTop: "9vh", padding: "2vh", width: "25%", backgroundColor: "#282c34", marginLeft: "38%" }}>Submit And Pay</Button>
                </Form>
            </div>
        )
    }
}
const mapStateToProps = state => {
    return {
        enterprise: state.enterprise.enterprise,
        // card: state.enterprise.card,
        // submit:state.enterprise.submitNewCard,
    }
}
const mapDispatchToProps = dispatch => {
    return {
        // submitNewCard: () => dispatch({ type: "SUBMIT", payload: true }),
    }
}
export default connect(mapStateToProps, mapDispatchToProps)(NewCard);
