import React, { Component } from 'react';
import 'semantic-ui-css/semantic.min.css';
import { Button, Form, Grid, Header, Image, Segment, FormInput } from 'semantic-ui-react';
import axios from 'axios';
class UsersSignUp extends Component {
    state = {
        name: '',
        tz: '',
        mail: '',
        password: '',
        phone: '',
        birthdate: ''
    }
    handleChangeName = (e) => {
        if (e.target.value.match("^[a-zA-Z ]*$") != null)
            this.setState({ name: e.target.value });
    }
    handleChangePhone = (e) => {
        if (e.target.value.match("^[0-9]*$") != null)
            this.setState({ phone: e.target.value });
    }
    handleChangeTz = (e) => {
        if (e.target.value.match("^[0-9]*$") != null)
            this.setState({ tz: e.target.value });
    }
    SignUp = () => {
        const user = {};     
        user["name"] = document.getElementsByName("Name")[0].value;
        // console.log( enterprise["Name"] );
        user["tz"] = document.getElementsByName("tz")[0].value;
        user["mail"] = document.getElementsByName("Email")[0].value;
        user["password"] = document.getElementsByName("Password")[0].value;
        user["phone"] = document.getElementsByName("Phone")[0].value;
        user["birthdate"] = document.getElementsByName("BirthDate")[0].value;

        console.log(user)
        axios.post(`http://localhost:64430/api/Users`, user).then(x => {
            this.props.history.replace('/users/login');
            console.log(x);
        }
        ).catch(x => {
            document.getElementById("message").innerHTML = x.response.data.Message;
        });
    }
    render() {
        return (<div>
            <Grid textAlign='center' style={{ height: '100vh' }} verticalAlign='middle'>
                <Grid.Column style={{ maxWidth: 450 }}>
                    <Header as='h2' color='teal' textAlign='center'>
                        <Image src='/Images/logo.png' /> Sign-Up to your account
                  </Header>
                    <Form size='large' onSubmit={this.SignUp}>
                        <Segment stacked>
                            <Form.Input
                                name="Name" icon='user' iconPosition="left" placeholder="Name" type="text" maxLength={40} value={this.state.name} onChange={this.handleChangeName}>
                            </Form.Input>
                            <Form.Input
                                name="tz" icon= "tag" iconPosition="left" placeholder="Identity" type="text" maxLength={9} value={this.state.tz} onChange={this.handleChangeTz}>
                            </Form.Input>
                            <Form.Input
                                name='Email' icon='envelope' iconPosition="left" placeholder='E-mail address' type="email" maxLength={50} value={this.state.mail} onChange={(event) => this.setState({ mail: event.target.value })}>
                            </Form.Input>
                            <Form.Input
                                name='Password' icon="lock" iconPosition="left" placeholder='Password' type="password" maxLength={15} value={this.state.password} onChange={(event) => this.setState({ password: event.target.value })}>
                            </Form.Input>
                            <Form.Input
                                name='Phone' icon="phone" iconPosition="left" placeholder='Phone' type="text" maxLength={10} value={this.state.phone} onChange={this.handleChangePhone}>
                            </Form.Input>
                            <Form.Input
                                name='BirthDate' icon="birthday cake" iconPosition="left" placeholder='BirthDate' type="date" value={this.state.data} onChange={(event) => this.setState({ date: event.target.value })}>
                            </Form.Input>
                            <div id="message" style={{ color: "red" }}></div>
                            <Button type="submit" color='teal' fluid size='large'>
                                Sign-Up
                            </Button>
                        </Segment>
                    </Form>
                </Grid.Column>
            </Grid>
        </div>
        )
    }
}
export default UsersSignUp;
