import React, { Component } from 'react'
import { Button, Item, Divider, Icon, Label, Grid, Segment } from 'semantic-ui-react';
import "semantic-ui-css"
import EnterpCard from "./EnterpCard"
import { getCards } from "../../../Store/actions/EnterpriseAction";
import { connect } from 'react-redux';
import "../../Public.css"
class EnterpCards extends Component {
    componentDidMount() {
        this.props.selectCards(this.props.enterprise.C_id);
    }
    render() {
        return (
            <div style={{ textAlign: "center"}}>
                {this.props.cards !== null ? <div style={{height:"100vh" }}><h1 style={{ fontSize: '20px', textAlign: "center", color: '#00b5ad' }}>| My Card/s |</h1>
                    <Item.Group divided style={{ marginLeft: "40vw",marginTop:"10vh" }}>{this.props.cards.map((x, i) => {
                        return <EnterpCard content={x.Type} key={i} img={x.Img}></EnterpCard>
                    })}   </Item.Group>
                    <Button animated="fade" icon labelPosition='left' style={{ color: "white",fontSize:"18px",backgroundColor:"#282c34",width:"35vw", marginBottom: "10vh",marginTop:"40vh", padding: "2vw" }}>
                        <Button.Content visible>Click Here For New Card</Button.Content>
                        <Button.Content hidden>Basic Price : 20$ For A Month</Button.Content>
                        <Icon name="hand point right outline" size="big" style={{borderRight:"solid 1px white"}}></Icon>
                    </Button>
                </div>
                    : "LOADING..."}
            </div>
        )
    }
}
const mapStateToProps = state => {
    return {
        enterprise: state.enterprise.enterprise,
        cards: state.enterprise.cards,
        loading: state.enterprise.loading,
    }
}
const mapDispatchToProps = dispatch => {
    return {
        selectCards: (id) => dispatch(getCards(id)),
    }
}
export default connect(mapStateToProps, mapDispatchToProps)(EnterpCards);
