import React from 'react'
import validValue from '../Service/ValidValue';
import { object } from 'prop-types';
const validForm = (form) => {
    for (let x in form) {
        console.log(form[x].validation)
        if (form[x].validation.required) {
            console.log(form[x].validation.required)
            if (form[x].value === "") {
                console.log(x)
                return { id: x, error:form[x].text + " is requried" };

            }
        }
        const valid = validValue(form[x].value, form[x].validation, x);
        if (valid !== null)
            return valid;
    };
    return null;
}
export default validForm;
