import React, { Component } from 'react'
import { Grid, Image, Header, Icon, Message } from "semantic-ui-react"
class lotery extends Component {
    state = {
        num1: null,
        num2: null,
        Addition: null,
        different: null,
        new: false,
        birthday: false,
        club_members: false,
        colum1: 16,
        colum2: 8,
        colum3: 8,
        enpty: false
    }
    componentDidMount() {
        for (let x in this.props.lotery) {
            if (this.props.lotery.length === 0) {
                this.setState({ empty: true })
            }
            else{

            }
            x.Type === 1 ?
                this.setState({ different: x.Expiration, new: true })
                : x.Type === 2 ?
                    this.setState({ different: null, birthday: true })
                    :
                    this.setState({ different: x.ExpireDate, club_members: true })
            x.LoteryType === "Discount" ?
                this.setState({ num1: x.Sum, num2: x.SumType + " Discount" })
                : x.LoteryType === "Plus Free" ?
                    this.setState({ num1: x.Num1 + " + ", num2: x.Num2 + " Lottry" })
                    :
                    this.setState({ num1: x.Num1 + " in ", num2: x.Num2 + " $" })
            this.setState({ Addition: x.Addition })
        }

    }
    render() {
        console.log(this.state)
        console.log(this.props)
        return  <div>
        {!this.state.loading? <Grid style={{ display: "inline-block", marginLeft: "5vw", width: "60vw", color: "white", textAlign: "center" }}>
           <Grid.Row style={{ height: "15vh" }}>
                   {this.state.new?  <Grid.Column width={this.state.colum1}>
                <Message style={{ height: "100%", width: "100%", backgroundColor: "gray", border: "solid white", borderRadius: "30px" }}
                        icon='inbox'
                        header="Lottery For Club Members"
                        content='Get the best news in your e-mail every day.'
                    />
                </Grid.Column>:""}
                {this.state.empty?<Grid.Column width={16}>
                    <Message style={{ height: "100%", width: "100%", backgroundColor: "gray", border: "solid white", borderRadius: "30px" }}
                        icon='inbox'
                        header="No lottery"
                    />
                </Grid.Column>:""}
            </Grid.Row>
            <Grid.Row style={{ height: "10vh", width: "60vw", color: "white", margin: "1vw" }}>
            {this.state.birthday? <Grid.Column width={this.state.colum2}>
                    <Message style={{ height: "100%", width: "100%", backgroundColor: "gray", border: "solid white", borderRadius: "30px" }}
                        icon='inbox'
                        header="Lottery For Club Members"
                        content='Get the best news in your e-mail every day.'
                    />
                </Grid.Column>:""}
                {this.state.club_members?   <Grid.Column width={this.state.colum3}>
                    <Message style={{ height: "100%", width: "100%", backgroundColor: "gray", border: "solid white", borderRadius: "30px" }}
                        icon='inbox'
                        header="Lottery For Club Members"
                        content='Get the best news in your e-mail every day.'
                    />
                </Grid.Column>:""}
            </Grid.Row>
        </Grid>:""}</div>

        // <div style={{display:"inline-block"}}>
