import validForm from "./ValidStateForm";
import validator from 'validator';
const onSignUp = (form) => {
    var obj;
    if (form["mail"].newPassword === true) {
        if (form["mail"].value === "") {
            obj = { id: "mail", error: "mail is requried" };
        }
        else
            if (!validator.isEmail(form["mail"].value))
                obj = { id: "mail", error: "mail is not valid" };
    }
    else {
        obj = validForm(form);
    }
    for (let x in form) {
        const inputError = { ...form[x] };
        inputError.valid = true;
        form[x] = inputError;
    }
    if (obj !== null) {
        const inputError = { ...form[obj.id] };
        inputError.valid = false;
        form[obj.id] = inputError;
        return obj;
    }
    return null;
}
export default onSignUp;
