import isIsraeliIdValid from 'israeli-id-validator'
import validator from 'validator';
const validValue = (value, validation, id, text) => {
    let valid = true;
    const obj=  { id: id, error: text + " is not valid" };
    console.log(validation);
    if (validation.mail) {
        if (!validator.isEmail(value))
            return obj;
    }
    if (validation.tz) {
        if (!validator.isIsraeliIdValid(value))
            return obj;
    }
    if (validation.password) {
        if (value.length < 6)
            return obj;
    }
    if (validation.phone) {
        if (!validator.isMobilePhone(value))
            return obj;
    }
    if (validation.url) {
        if (!validator.isURL(value))
            return obj;
    }
    // if (tergets.minLength) {
    //     valid = value.length < tergets['maxLength'] && valid;
    // }
    return null;
}
export default validValue;
