import React, { Component, createRef } from "react";
import { NavLink, withRouter } from 'react-router-dom';
import { connect } from 'react-redux'
import {
    Icon,
    Button,
    Menu,
    Sidebar, Sticky, Ref
} from "semantic-ui-react";
class MySidebar extends Component {
    navlinks = [];
    state = {
        visible: false,
        routeIn: false
    };
    componentDidUpdate() {
        switch (this.state.routeIn) {
            case false: {
                if (this.props.history.location.pathname !== "/")
                    this.setState({ routeIn: true });
                break;
            }
            case true: {
                if (this.props.history.location.pathname === "/")
                    this.setState({ routeIn: false });
                break;
            }
            default: break;
        }
        console.log(this.props)
        if (this.props.user !== null)
            this.navlinks = [
                { value: "My Cards", path: "/users/cards" },
                { value: "New Cards", path: "/users/new" },
                { value: "Update Profile", path: "/users/ProfileUpdate" },
                { value: "Discontact", path: "" }
            ]
        else
            if (this.props.enterprise !== null) {
                this.navlinks = [
                    { value: "My Cards", path: "/enterprises/enterpCards" },
                    { value: "Update Profile", path: "/enterprises/ProfileUpdate" },
                    { value: "New Card", path: "/enterprises/newCard" },
                    { value: "Agreement and price list", path: "/agreement" },
                    { value: "Discontact", path: "" }]
            }
            else
                if (this.props.history.location.pathname === "/") {
                    this.navlinks = [{ value: "Home", path: "" },
                    { value: "About", path: "about" },
                    { value: "Why it's worth me", path: "about_user" },
                    { value: "Why it's worth my enterprise", path: "about_enterprise" },
                    { value: "Enterprises", path: "stores" },
                    { value: "Contact us", path: "contact" }]
                }
                else {
                    this.navlinks = [{ value: "Home", path: "/" }]
                }
    }
    handleHideClick = () => this.setState({ visible: false });
    handleShowClick = () => this.setState({ visible: true });
    handleSidebarHide = () => this.setState({ visible: false });
    render() {
        const contextRef = createRef();
        const { visible } = this.state;
        return (
            <div>
                <Button id="sidebarout" style={{ position: "fixed", backgroundColor: " #3b393a", top: "0px" }} onClick={this.handleShowClick}>|||</Button>
                <Sidebar.Pushable style={{ transform: "none" }}>
                    <Sidebar style={{ position: "fixed", top: "0px" }}
                        as={Menu}
                        animation="overlay"
                        icon="labeled"
                        inverted
                        onHide={this.handleSidebarHide}
                        vertical
                        visible={visible}
                        width="large" >
                        <Button id="sidebarin" onClick={this.handleHideClick}>X</Button>
                        {this.navlinks.map((x, i) => {
                            return <Menu.Item style={{ padding: "3vh" }} as='a'>{this.state.routeIn === false ?
                                <a href={"#" + x.path}>{x.value}</a> :
                                <NavLink style={{ padding: "3vh" }} to={x.path}>{x.value}</NavLink>
                            }
                            </Menu.Item>
                        })}
                    </Sidebar>
                </Sidebar.Pushable>
            </div>

        );
    }
}
const mapStateToProps = state => {
    return {
        user: state.user.user,
        enterprise: state.enterprise.enterprise,
    }
}
export default withRouter(connect(mapStateToProps, null)(MySidebar));
