import React, { Component } from 'react'
import { Button, Item, Divider, Icon, Label,Grid,Segment } from 'semantic-ui-react';
import "semantic-ui-css"
import axios from 'axios';
import { getEnterpriseById } from "../../../Store/actions/EnterpriseAction";
import EnterpCard from "./EnterpCard"
import { getCards } from "../../../Store/actions/EnterpriseAction";
import { connect } from 'react-redux';
import "../../Public.css"
class EnterpCards extends Component {
    componentDidMount() {
        this.props.selectCards(this.props.enterprise.C_id);
    }
    render() {
        return (
            <div style={{ textAlign: "center" }}>
                {this.props.cards !== null ? <div><h1 style={{ fontSize: '20px', textAlign: "center", color: '#00b5ad' }}>| My Card/s |</h1>
                    <Item.Group divided style={{ marginLeft: "40vw" }}>{this.props.cards.map((x, i) => {
                        return <EnterpCard content={x.Type} key={i} img={x.Img}></EnterpCard>
                    })}   </Item.Group>
                    <Grid>
                        <Grid.Column width={8}>
                            <Segment attached='top'>
                                The button below accepts clicks with mouse and key presses with
                                Space Bar or Enter.
          </Segment>
                            <Button
                                attached='bottom'
                                content='Click'
                                onClick={this.handleClick}
                                onKeyPress={this.handleKeyPress}
                            />
                        </Grid.Column></Grid>
                        {/* <Grid.Column width={8}> */}
                            {/* <Segment>
                                <pre style={{ height: 100, overflowY: 'scroll' }}>
                                    {log.map((e, i) => (
                                        <p key={i}>{e}</p>
                                    ))}
                                </pre>
                            </Segment> */}
                        {/* </Grid.Column> */}
                    
                </div>
                    : "LOADING..."}
            </div>
        )
    }
}
const mapStateToProps = state => {
    return {
        enterprise: state.enterprise.enterprise,
        cards: state.enterprise.cards,
        loading: state.enterprise.loading,
    }
}
const mapDispatchToProps = dispatch => {
    return {
        selectCards: (id) => dispatch(getCards(id)),
    }
}
export default connect(mapStateToProps, mapDispatchToProps)(EnterpCards);
