import 'semantic-ui-css/semantic.min.css';
import { Input, Select, Form, Dropdown, Grid, Segment, Radio, Header, Image, TextArea, Popup, Button, Icon } from 'semantic-ui-react';
class Lotery extends Component {
    state = {
        statusLotery: -1,
        loteryName: "",
        loteryTypes: [
            { key: 'Discount', text: 'Discount', value: 'Discount' },
            { key: "Plus Free", text: "Plus Free", value: "Plus Free" },
            { key: "X in Y", text: "X in Y", value: "X in Y" }],
        sumTypes: [
            { key: '%', text: '%', value: '%' },
            { key: '$', text: '$', value: '$' }
        ],
        form: {
            loteryType: "Discount",
            sum: "",
            sumType: "",
            num1: "",
            num2: "",
            addition: "",
            expireDate: "",
        },

    }
    hanleSelctOnChange = (event, value) => {
        const form = { ...this.state.form }
        form.loteryType = value;
        form.expireDate = "";
        // this.setState({ loteryType: value });
        this.setState({ form: form });


    }
    render() {
        return (
            <div>
                <Form>
                    <Form.Select label="Lottery Types" options={this.state.loteryTypes}
                        defaultValue="Discount"
                        onChange={(event, { value }) => this.hanleSelctOnChange(event, value)}></Form.Select>
                    {this.state.form.loteryType === "Discount" ? <Form.Input
                        label="Details"
                        type="number"
                        labelPosition='right'
                        action={
                            <Dropdown button basic floating options={this.state.sumTypes} defaultValue='$' />}></Form.Input> : this.state.form.loteryType === "Plus Free" ?
                            <Form.Input> <Input></Input> <bold> + </bold><Input></Input></Form.Input> :
                            <Form.Input> <Input></Input> In <Input></Input></Form.Input>}
                    <Form.Input type="date" label="Expire Date" value={this.state.form.expireDate} onChange={(e, { value }) => {
                        const form = { ...this.state.form }
                        form["expireDate"] = value
                        this.setState({ form: form })
                    }} >
                    </Form.Input>
                    <Form.Input label="Addition" type="text" placeholder="Addition" maxLength={50}></Form.Input>
                </Form>
