import React, { Component } from 'react'
import { Menu, Segment } from "semantic-ui-react"
import { NavLink } from 'react-router-dom';
import { connect } from 'react-redux';
class Header extends Component {
    state = {
        navlinks: [{ value: "Home", path: "/" }],
        url: "home"
    }

    componentDidUpdate() {
        if ((this.props.user !== null && this.state.url === "user") || (this.props.enterprise !== null && this.state.url === "enterprise")||(this.props.user === null && this.props.enterprise === null&&this.state.url === "home"))
        {    const navlinks = [{ value: "Home", path: "/" }]
        console.log("update", this.props.user)
        var url="home";
        if (this.props.user !== null) {
            navlinks = [
                { value: "My Cards", path: "/users/cards" },
                { value: "New Cards", path: "/users/new" },
                { value: "Update Profile", path: "/users/ProfileUpdate" },
                { value: "Discontact", path: "" }
            ]
            url ="user";
        }
        if (this.props.enterprise !== null) {
            navlinks = [
                { value: "My Cards", path: "/enterprises/enterpCards" },
                { value: "Update Profile", path: "/enterprises/ProfileUpdate" },
                { value: "New Card", path: "/enterprises/newCard" },
                { value: "Agreement and price list", path: "/agreement" },
                { value: "Discontact", path: "" }]
                url="enterprise"
        }
        this.setState({ navlinks: navlinks,url:url })
    }}
    render() {
        return (
            <div style={{ position: "fixed", width: "100vw", zIndex: "300", boxShadow: " 5px 10px 18px #888888", backgroundColor: "#191a19" }}>
                <Menu inverted>
                    {this.navlinks.map((x, i) => {
                        return <NavLink to={x.path} style={{ textAlign: "center", padding: "1.5%", fontSize: "17px" }}><Menu.Item
                            link
                            name={x.value}
                        >
                        </Menu.Item>
                        </NavLink>
                    })}
                </Menu>
            </div>
        );
    }
}
const mapStateToProps = state => {
    return {
        user: state.user.user,
        enterprise: state.enterprise.enterprise,
    }
}
export default connect(mapStateToProps, null)(Header);
