import React, { Component } from 'react'
import { Button, Form, Grid, Header, Image, Loader, Dimmer, Segment, Message } from 'semantic-ui-react';
import 'semantic-ui-css/semantic.min.css';
import axios from 'axios';
import { getEnterpriseById } from "../../../Store/actions/EnterpriseAction";
import EnterpCards from "../EnterpCards/EnterpCards"
import { connect } from 'react-redux'
class Main extends Component {
    state = {
        loaded: false,
        enterpId: null,
        permission: false,
        timeIsHoverError: "",
    }
    search = this.props.location.search;
    params = new URLSearchParams(this.search);
    key = this.params.get('key');
    componentDidMount() {
        axios.get(`http://localhost:64430/api/SendMail?key=${this.key}`).then(x => {
            this.setState({ enterpId: x.data, permission: true });
            this.props.selectEnterprise(this.state.enterpId);
            this.setState({loaded:true});
        }).catch(x => this.setState({ timeIsHoverError: x.response.data.Message, permission: false ,loaded:true}));
    }
    render() {
        return (
            <div>
                {this.state.loaded?this.state.permission ?  this.props.history.replace("/enterprises/enterpCards")     
                : <Message size='massive' style={{textAlign:"center"}}> <Message.Header>Error!</Message.Header>
                        <p>{this.state.timeIsHoverError}</p>
                    </Message>:"LOADING..."}
            </div>
        )
    }
}
const mapDispatchToProps = dispatch => {
    return {
        selectEnterprise: (id) => dispatch(getEnterpriseById(id)),
    }
}
export default connect(null, mapDispatchToProps)(Main);
