import React, { Component } from 'react'
import 'semantic-ui-css/semantic.min.css';
import { Button, Form, Grid, Header, Image, Segment, FormInput, Input, TextArea, Label, Divider, GridColumn } from 'semantic-ui-react';
class ContactUs extends Component {
    state = {
        form: {
            name: {
                type: "text",
                icon: "user",
                text: "Name",
                value: "",
                maxLength: 40,
                validition: {
                    required: true,
                    pattern: /^[a-zA-Z ]*$/
                },
                error: "The name is incorrect",
                valid: false,
                toched: false
            },
            mail: {
                type: "text",
                icon: "envelope",
                text: "E-mail address",
                value: "",
                maxLength: 50,
                validition: {
                    required: true,
                    pattern: /^/
                    // pattern: /^\w+([\.-]?\w+)*@\w+([\.-]?\w+)*(\.\w{2,3})+$/,
                },
                error: "The email is incorrect",
                valid: false,
                toched: false
            },
            //     <Form.Input
            //     className='Phone' icon="phone" iconPosition="left" placeholder='Phone' type="text" maxLength={10} value={this.state.phone} onChange={this.handleChangePhone}>
            // </Form.Input>
            phone: {
                type: "text",
                icon: "phone",
                text: "Phone",
                value: "",
                maxLength: 10,
                validition: {
                    required: true,
                    pattern: /^[0-9]*$/
                },
                error: "The phone is incorrect",
                valid: false,
                toched: false
            },
        },
        validForm: false,
    }
    render() {
        const array = [];
        for (let x in this.state.form) {
            array.push({ id: x, config: this.state.form[x] })
        }
        return (<div>
            <Grid textAlign='center' celled='internally' style={{ height: '100vh' }} verticalAlign='middle'>
                <Grid.Row>
                    <Grid.Column width={12} style={{}}>
                        <Form size='large' onSubmit={this.SignUp}>
                            <Grid.Column width={12}>
                                <Form.Input placeholder='Name' width={6} />
                                <Form.Input placeholder='Email' width={6} />
                                <Form.Input placeholder='Phone' width={6} />
                            </Grid.Column>
                            {/* <Segment stacked> */}
                            {array.map((x, i) => {
                                return <Form.Input key={i} iconPosition="left"
                                    maxLength={x.config.maxLength} type={x.config.type}
                                    // onChange={event => this.onChangeHendler(event, x.id)}
                                    className={(!x.config.valid ? x.id : "error")}
                                    placeholder={x.config.text} icon={x.config.icon}>
                                </Form.Input>
                            })}
                            <Form.TextArea placeholder="Description"></Form.TextArea>
                            <div id="message" style={{ color: "red" }}></div>
                            <Button type="submit" color='teal' fluid size='large'>
                                Contuct me
                                </Button>
                            {/* </Segment> */}
                        </Form>
                    </Grid.Column>

                    <Grid.Column width={2} style={{ minWidth: 150 }} verticalAlign='middle' >
                        <Label style={{ display: "block" }}>Email:easyclub100@gmail.com</Label>
                        <Label style={{ display: "block" }}>Fax:03-9258478</Label>
                        <Label style={{ display: "block" }}>Phone:0527689412</Label>
                    </Grid.Column>
                </Grid.Row>
            </Grid>
            {/* <Form>
                <Form.Group>
                    <Form.Input placeholder='Name' width={4} />
                    <Form.Input placeholder='Email' width={4} />
                    <Form.Input placeholder='Phone' width={4} />
                </Form.Group>
                <Form.Group>
                <Form.TextArea placeholder="Description" width={12}></Form.TextArea>
                </Form.Group>
                <Form.Group>
                <Form.Button width={12}>Contact Me</Form.Button>
                </Form.Group>
            </Form>
            <Divider ></Divider>
            <Form.Group>
                <Label value='8 Wide' width={8}>vxv</Label>
                <Label value='8 Wide' width={8}>vxv</Label>
                <Label value='8 Wide' width={8}>vxv</Label>
            </Form.Group> */}

        </div>

        )
    }
}
export default ContactUs
