import React, { Component } from "react";
import {
  Button,
  Header,
  Icon,
  Image,
  Menu,
  Segment,
  Sidebar
} from "semantic-ui-react";

class MySidebar extends Component {
  state = { visible: false };

  handleSidebarHide = () => this.setState({ visible: false });
  handleSidebarToggle = () =>
    this.setState(prevState => ({ visible: !prevState.visible }));

  render() {
    const { visible } = this.state;
    return (
      <div>
        <Sidebar.Pushable as={Segment.Group} raised>
          <Sidebar
            as={Menu}
            animation='overlay'
            icon='labeled'
            inverted
            // onHide={() => setVisible(false)}
            vertical
            // target={segmentRef}
            visible={visible}
            width='thin'
          >
            <Menu.Item as='a'>Home</Menu.Item>
            <Menu.Item as='a'>Games</Menu.Item>
            <Menu.Item as='a'>Channels</Menu.Item>
          </Sidebar>

          {/* <Ref innerRef={segmentRef}>
            <Segment secondary>
              <Header as='h3'>Clickable area</Header>
              <p>When you will click there, the sidebar will be closed.</p>
            </Segment>
          </Ref> */}

          <Segment>
          <Button.Group>
          <Button onClick={this.handleSidebarToggle} style={{position:"absolute"}}>|||</Button>
        </Button.Group>
        <div id="home">
                <h1 style={{padding:"40vh",color:"white"}}>YOUR CLUB CARD MANAGEMENT APPLICATION</h1>
            </div>
          </Segment>
        </Sidebar.Pushable>
      </div>
    );
  }
}
export default MySidebar
