import React, { Component } from 'react';
import 'semantic-ui-css/semantic.min.css';
import { Button, Form, Grid, Header, Image, Segment, Dimmer, Loader, Label, Checkbox } from 'semantic-ui-react';
import axios from 'axios';
import onChangeInput from "../../Service/onChangeInput";
import onSignUp from "../../Service/onSubmit";
import validCheckbox from "../../Service/validCheckbox";
import { connect } from 'react-redux'
class EnterpriseSignUp extends Component {
    state = {
        form: {
            Name: {
                type: "text",
                icon: "user",
                text: "Name",
                value: "",
                maxLength: 40,
                validation: {
                    required: true,
                    pattern: /^[a-zA-Z&.@ ]*$/,
                },
                valid: false,
            },
            Email: {
                type: "text",
                icon: "envelope",
                text: "E-mail address",
                value: "",
                maxLength: 50,
                validation: {
                    required: true,
                    pattern: /^[0-9a-zA-Z.+*%#!@]+$/,
                    mail: true
                },
                valid: false,
            },
            Password: {
                type: "Password",
                icon: "lock",
                text: "Password",
                value: "",
                maxLength: 15,
                validation: {
                    required: true,
                    pattern: /^[0-9a-zA-Z]+$/,
                    Password: true
                },
                valid: false,
            },
            Phone: {
                type: "text",
                icon: "phone",
                text: "Phone",
                value: "",
                maxLength: 10,
                validation: {
                    required: true,
                    pattern: /^[0-9]*$/,
                    phone: true
                },
                valid: false,
            },
            Url: {
                type: "text",
                icon: "share",
                text: "Url",
                value: "",
                maxLength: 100,
                validation: {
                    required: true,
                    pattern: /^/,
                    url: true
                },
                valid: false,
            },
        },
        checkbox: false,
        trySend: false,
        loading: false,
        errorMessage: "",
        update: false
    }
    onChangeHendler = (e, id) => {
        this.setState({ trySend: false,errorMessage:"" })
        const form = { ...this.state.form };
        const inputChanged = { ...form[id] };
        onChangeInput(e, inputChanged, form, id);
        this.setState({ form: form })
    }
componentWillMount() {
        // if (this.props.location.pathname === '/enterprises/ProfileUpdate')
        //     this.setState({ update: true })
        // else
        //     this.setState({ update: false })
    {this.props.location.pathname==="/enterprises/ProfileUpdate"?this.setState({ update: true }):this.setState({ update: false })}
    }
    SignUp = () => {
        const form = { ...this.state.form };
        var obj = onSignUp(form)
        if (obj === null && !this.state.update)
            obj = validCheckbox(this.state.checkbox)
        if (obj !== null) {
            this.setState({ form: form, trySend: true, errorMessage: obj.error });
        }
        else {
            this.setState({ loading: true });
            const data = {};
            for (let x in this.state.form) {
                data[x] = this.state.form[x].value;
            }
            axios.post(`http://localhost:64430/api/Enterprises`, data).then(x => {
                this.props.history.replace('/enterprises/login');
            }).catch(x => {
                this.setState({ loading: false, errorMessage: x.response.data.Message });
            });
        }
    }
    render() {
        const array = [];
        for (let x in this.state.form) {
            array.push({ id: x, config: this.state.form[x] })
        }
        return (<div>
            <Grid textAlign='center' style={{ height: '100vh' }} verticalAlign='middle'>
                <Grid.Column style={{ maxWidth: 450 }}>
                    <Header as='h2' color='teal' textAlign='center'>
                        <Image src='/Images/logo.png' />
                        {this.state.update?" Update Profil":"Sign-Up to your account"}
                  </Header>
                    <Form size='large' onSubmit={this.SignUp}>
                        <Segment stacked>
                            {array.map((x, i) => {
                                return <Form.Input
                                    key={i} iconPosition="left"
                                    maxLength={x.config.maxLength}
                                    type={x.config.type}
                                    onChange={event => this.onChangeHendler(event, x.id)}
                                    className={this.state.trySend && !this.state.form[x.id].valid ? "error" : x.id}
                                    placeholder={x.config.text + "*"}
                                    value={this.state.update?this.props.enterprise[x.id]:this.state.form[x.id].value}
                                    icon={x.config.icon}>
                                </Form.Input>
                            })}
                            <div id="message" style={{ color: "red" }}>{this.state.errorMessage}</div>
                            {this.state.update? "":<Checkbox
                                checked={this.state.checkbox} onChange={e => this.setState({ checkbox: !this.state.checkbox })} label={<label>I accept the <a href="/agreement" target="_blank">Terms of Service</a></label>} />}
                            <Button type="submit" color='teal' fluid size='large'>
                            {this.state.update?" Update":"Sign-Up"}
                            </Button>
                        </Segment>
                    </Form>
                </Grid.Column>
            </Grid>
            <Dimmer active={this.state.loading} inverted>
                <Loader size='large'>Loading</Loader>
            </Dimmer>
        </div >
        )
    }
}
const mapStateToProps = state => {
    return {
      enterprise: state.enterprise.enterprise
    }
  }
export default connect(mapStateToProps,null)(EnterpriseSignUp);
