import React, { Component } from 'react'
import { Segment, List, Divider } from "semantic-ui-react"
class lotery extends Component {
    state = {
        num1: null,
        num2: null,
        Addition: null,
        different: null
    }
    componentDidMount() {
        {
            this.props.Type === 1 ?
                this.setState({ different: this.props.Expiration })
                : this.props.Type === 2 ?
                    this.setState({ different: null })
                    :
                    this.setState({ different: this.props.ExpireDate })
        }
        {

            this.props.LoteryType === "Discount" ?
                this.setState({ num1: this.props.Sum, num2: this.props.SumType + " Discount" })
                : this.props.LoteryType === "Plus Free" ?
                    this.setState({ num1: this.props.Num1 + " + ", num2: this.props.Num2 + " Lottry" })
                    :
                    this.setState({ num1: this.props.Num1 + " in ", num2: this.props.Num2 + " $" })
        }
        this.setState({ Addition: this.props.Addition })
    }
    render() {
        return   <List.Item style={{fontSize:"18px",color:"white",backgroundColor:"black",border:"groove 1px",marginBottom:"2vh",padding:"2vh",borderRadius:"20px"}}>  
                    <List.Icon size="big" name={this.props.Type === 1 ? "user plus" : this.props.Type === 2 ? "birthday" : "credit card outline"}></List.Icon>
                    <List.Content>
                        <List.Header style={{fontSize:"20px",display:"inline-block",color:"white"}}>{this.props.Type === 1 ? "New Member :" : this.props.Type === 2 ? "Birthday :" : "Club Members :"}</List.Header>
                        You have {this.state.num1}{this.state.num2}
                        <div>{this.state.Addition !== null ? "*" + this.state.Addition : ""}</div>
                    </List.Content>

                </List.Item>
    }
}
export default lotery;
