import React, { Component } from 'react';
import Lotery from '../Lotery/Lotery';
// import { Button, Form, Grid, Header, Image, Message, Segment, Checkbox, Select,Input } from 'semantic-ui-react';
import { NavLink, withRouter } from 'react-router-dom';
import { InputFile } from 'semantic-ui-react-input-file';
import { Route, Switch, Redirect, Router } from 'react-router-dom';
import { Input, Form } from 'semantic-ui-react-form-validator'
import {Button} from 'semantic-ui-react';
class NewCard extends Component {

    //         { name: 'birthday', icon: "birthday cake", placeholder: "Prise for birthday", type: "Number",step:"0.1"},
    //     ],
    //     status_new: false,
    //     status_birthday: false,
    // }
    state = {
        form: {
            password: {
                icon: "address card",
                text: "Password",
                type: "text",
                maxLength: 15,

            },
            mail: {
                icon: "envelope",
                text: "Email",
                type: "text",
                maxLength: 50,

            },
            cost: {
                icon: 'dollar sign',
                text: 'Card Price',
                type: 'text',
                maxLength: 7,
            },
            img: {
                icon: "images outline",
                text: "Img",
                type: "text",
                maxLength: 50,
            },
            type: {
                icon: "paper plane",
                text: "Type",
                type: "text",
                maxLength: 50,

            },
            points: {
                icon: "dot circle outline",
                text: "points",
                type: "Number",
                maxLength: 5,
            },
            money: {
                icon: "shekel sign",
                text: "money",
                type: "Number",
                step: "0.1",
                maxLength: 5,
            },
        },
        new: false,
        birthday: false


    }
    onSubmitHandler = () => {

    }
    genderOptions = [
        { key: '%', text: '%', value: '%' },
        { key: 'Points', text: 'points', value: 'Points' },
    ]
    render() {
        const array = [];
        for (let x in this.state.form) {
            array.push({ id: x, config: this.state.form[x] })
        }
        return (
            <Form
                ref="form"
                onSubmit={this.onSubmit}
            >
                <Input
                    type="text"
                    label="Test Input"
                    onChange={(e) => { this.setState({ value: e.target.value }) }}
                    value={this.state.value}
                    validators={['required']}
                    errorMessages={['this field is required']}
                    width={6}
                />

                <Button color="teal">Submit</Button>
      </Form>
        );
    }
}
export default NewCard;
