import axios from 'axios';
export async function getUser(mail, password) {
    axios.get(`http://localhost:64430/api/Users?password=${password}&email=${mail}`).then(x => {
         props.history.replace('cards');
        return x;
    }).catch(x => {
        throw x;
    });
}
