import React, { Component } from "react";
import { NavLink } from 'react-router-dom';
import {
    Button,
    Header,
    Icon,
    Image,
    Menu,
    Segment,
    Sidebar
} from "semantic-ui-react";
import Login from '../Login/Login';
import { Route, Switch, Redirect, Router } from 'react-router-dom';
import UsersSignUp from '../Users/SignUp/SignUp';
import UserApplication from '../Users/Application/Application';
import Stores from '../MyStores/MyStores';
import Report from '../Users/Report/Report';
import UserNewCard from '../Users/NewCard/NewCard';
import EnterpApplication from '../Enterprise/Application/Application';
import EnterpNewCard from '../Enterprise/NewCard/NewCard';
import Update from '../Enterprise/Update/Update';
import Cards from '../Users/Cards/Cards';
import NewCard from '../Users/NewCard/NewCard';
import { withRouter } from 'react-router-dom'
import Agreement from '../Enterprise/Agreement/Agreement';
import Clearing from '../Clearing/Clearing';
import EnterpriseSingUp from '../Enterprise/SignUp//SignUp';
import NewPassword from '../NewPassword/NewPassword';
import ContactUs from '../ContactUs/ContactUs';
import Chat from '../Chat/Chat';
import Lotery from '../Enterprise/Lotery/Lotery';
import Home from "../Home/Home"
class MySidebar extends Component {
    state = { visible: false };

    handleSidebarHide = () => this.setState({ visible: false });
    handleSidebarToggle = () =>
        this.setState(prevState => ({ visible: !prevState.visible }));

    render() {
        const { visible } = this.state;
        return (
            <div>
                <Sidebar.Pushable as={Segment.Group} raised >
                    <Sidebar style={{ backgroundColor: "transparent", borderRight: "solid 1px rgba(255,255,255,.08)" }}
                        as={Menu}
                        animation='overlay'
                        icon='labeled'
                        inverted
                        vertical
                        visible={visible}
                        width='thin'
                    >
                        <div id="sidebarin" onClick={this.handleSidebarHide}>X</div>
                        <Menu.Item as='a'><NavLink to="/">Home</NavLink></Menu.Item>
                        <Menu.Item as='a'><NavLink to="/users/login">User login</NavLink></Menu.Item>
                        <Menu.Item as='a'><NavLink to="/enterprises/login">Enterprise login</NavLink></Menu.Item>
                        <Menu.Item as='a'><NavLink to="/stores">Customers</NavLink></Menu.Item>
                        <Menu.Item as='a'><NavLink to="/stores">About</NavLink></Menu.Item>
                        <Menu.Item as='a'><NavLink to="/stores">Contact us</NavLink></Menu.Item>
                        <Menu.Item as='a'><NavLink to="/stores">Why it's worth me</NavLink></Menu.Item>
                        <Menu.Item as='a'><NavLink to="/stores">Why it's worth my enterprise</NavLink></Menu.Item>
                    </Sidebar>
                    {!this.state.visible ? <Button id="sidebarout" onClick={this.handleSidebarToggle} style={{ position: "absolute" }}>|||</Button> : ""}
                    <Switch>
                        <Route path="/users/login" component={Login} />
                        <Route path="/users/newpassword" component={NewPassword}></Route>
                        <Route path="/users/cards" component={Cards}></Route>
                        <Route path="/users/new" component={UserNewCard} exact />
                        <Route path="/users/report" component={Report} />
                        <Route path="/users/signUp" component={UsersSignUp} />
                        <Route path="/enterprises/login" component={Login} />
                        <Route path="/enterprises/signUp" component={EnterpriseSingUp} />
                        <Route path="/enterprises/new" component={EnterpNewCard} />
                        <Route path="/stores" component={Stores} />
                        <Route path="/enterprises/update" component={Update} />
                        <Route path="/enterprises/newpassword" component={NewPassword}></Route>
                        <Route path="/enterprises/lotery" component={Lotery}></Route>
                        <Route path="" component={Home} exact></Route>
                    </Switch>
                </Sidebar.Pushable>
                
            </div>
        );
    }
}
export default MySidebar
