import React, { Component, createRef } from "react";
import { NavLink } from 'react-router-dom';
import {
    Button,
    Menu,
    Sidebar, Sticky, Ref
} from "semantic-ui-react";
class MySidebar extends Component {
    state = {
        visible: false,
        navlinks: {
            1: {
                value: "Home",
                path: "home",
            },
            // 2: {
            //     value: "User login",
            //     path: "users/login"
            // },
            // 3: {
            //     value: "Enterprise login",
            //     path: "enterprises/login"
            // },
            4: {
                value: "About",
                path: "about"
            },
            5: {
                value: "Why it's worth me",
                path: "/"
            },
            6: {
                value: "Why it's worth my enterprise",
                path: "/"
            },
            7: {
                value: "Enterprises",
                path: "stores"
            },

            8: {
                value: "Contact us",
                path: "contact"
            },
        }
    };
    handleHideClick = () => this.setState({ visible: false });
    handleShowClick = () => this.setState({ visible: true });
    handleSidebarHide = () => this.setState({ visible: false });

    render() {
        const contextRef = createRef();
        const array = [];
        for (let x in this.state.navlinks) {
            array.push({ id: x, config: this.state.navlinks[x] })
        }
        const { visible } = this.state;
        return (
            <div>
                <Ref innerRef={contextRef}>
                <Sidebar.Pushable style={{ transform: "none" }}>
                    <Sticky>
                        <div style={{ backgroundColor: "#282c34", textAlign: "left", zIndex: "50", height: "7vh"}} >
                       {!this.props.location.pathname==="/users/login"?     <Button id="sidebarout" onClick={this.handleShowClick}>|||</Button>:""}
                        </div>
                        <Sidebar style={{ backgroundColor: "#282c34" }}
                            as={Menu}
                            animation="overlay"
                            icon="labeled"
                            inverted
                            onHide={this.handleSidebarHide}
                            vertical
                            visible={visible}
                            width="thin" >
                            <Button id="sidebarin" onClick={this.handleHideClick}>X</Button>
                            {array.map((x, i) => {
                                return <Menu.Item as='a' href={"#"+x.config.path}>{x.config.value}</Menu.Item>
                            })}
                        </Sidebar>
                    </Sticky>
                </Sidebar.Pushable>
                </Ref>
            </div>

        );
    }
}

export default MySidebar
