import React, { Component } from 'react';
import { Form, Grid, Header, Input, Popup, Divider } from 'semantic-ui-react';
import { InputFile } from 'semantic-ui-react-input-file';
import { connect } from 'react-redux'
import { selectCard } from "../../../Store/actions/EnterpriseAction"
import axios from 'axios';
class CardDetails extends Component {
    state = {
        form: {
            Cost: {
                label: "Price For Customer",
                icon: "$",
                type: 'number',
                step: 5,
                text: "Card Price *",
                value: "",
                validation: {
                    required: true,
                }
            },
            CountPoints: {
                label: "Count CountPoints",
                icon: " = 1 $",
                type: 'number',
                text: "Count CountPoints *",
                value: "",
                validation: {
                    required: true,
                }
            },
            Type: {
                options: [{ key: "Basic", text: 'Basic card', value: 'Basic card'},
                { key: "Primium", text: 'Primium card', value: 'Primium card' }],
                label: "Card Type",
                text: "Type *",
                value: "",
                validation: {
                    required: true,
                }
            },
            // , disabled: this.props.cards[1].Type===this.value? true : false?
            Img: {
                label: 'Enterprise Image',
                value: "",
                text: "select Img",
                validation: {
                    required: false,
                    img: true,
                }
            }
        },
        fileName: "",
    }
    hanleSelectOnChange = (event, { value }) => {
        const form = { ...this.state.form }
        const input = { ...form["Type"] }
        input.value = value;
        form["Type"] = input
        this.setState({ form: form })
    }
    convertImg = (event) => {
        const file = event.target.files[0];
        if (file) {
            var name = file.name;
            const reader = new FileReader();
            reader.readAsDataURL(file);
            console.log(reader)
            reader.onload = () => {
                this.onChangeHandler(String(reader.result), "Img")
                this.setState({ fileName: name })
            }
        }
    }
    onChangeHandler = (value, id) => {
        const form = { ...this.state.form }
        const input = { ...form[id] }
        if (input.type === "number") {
            input.value = parseInt(value);
        }
        else {
            input.value = value;
        }
        form[id] = input
        this.setState({ form: form })
        const card = {}
        for (let x in this.state.form) {
            card[x] = this.state.form[x].value;
        }
        this.props.changed(card);
    }
    componentDidUpdate() {
        if (this.props.submit && this.props.card === null) {
            const data = {};
            for (let x in this.state.form) {
                data[x] = this.state.form[x].value;
            }
            this.props.selectCard(data)
        }
    }
    render() {
        console.log(this.props.cards)
        const form = this.state.form;
        const array = [];
        for (let x in this.state.form) {
            array.push({ id: x, config: this.state.form[x] })
        }
        return (<div style={{ marginLeft: "1vw" }}>
            <Header as='h2' color='teal' textAlign='left'>Card Details</Header>
            <Divider></Divider>
            <Form>
                <Grid><Grid.Column><Form.Group>
                    <Form.Input label={form.Cost.label}>
                        <Input label={form.Cost.icon} min={0} labelPosition="right" type={form.Cost.type} onChange={event => this.onChangeHandler(event.target.value, "Cost")} value={form.Cost.value}
                            // className={this.state.trySend && !this.state.form[x.id].valid ? "error" : x.id} 
                            placeholder={form.Cost.text}
                            step={form.Cost.step}
                        /></Form.Input>
                    <Form.Input label={form.CountPoints.label}><Popup content="for example : 5 point =1$" trigger={<Input min={1} onChange={event => this.onChangeHandler(event.target.value, "CountPoints")} value={form.CountPoints.value} type={form.CountPoints.type} label={form.CountPoints.icon} labelPosition="right" placeholder={form.CountPoints.text} />}
                    />
                    </Form.Input>
                    <Form.Select label={form.Type.label} placeholder={form.Type.text} options={form.Type.options} onChange={this.hanleSelectOnChange} ></Form.Select>
                    <Form.Input label={form.Img.label}>
                        <InputFile
                            button={{
                                labelPosition: "left",
                                label: <Input placeholder={form.Img.text} value={this.state.fileName !== "" ? this.state.fileName : ""}></Input>
                            }}
                            input={{
                                onChange: this.convertImg,
                            }} /></Form.Input>
                </Form.Group></Grid.Column></Grid></Form>
        </div>
        )
    }
}
const mapStateToProps = state => {
    return {
        card: state.enterprise.card,
        cards: state.enterprise.cards,
    }
}
const mapDispatchToProps = dispatch => {
    return {
        // selectCard: (card) => dispatch(selectCard(card)),
    }
}
export default connect(mapStateToProps, mapDispatchToProps)(CardDetails);
