import React, { Component } from 'react'
import { Button, Form, Grid, Header, Image, Loader, Dimmer, Segment, Message } from 'semantic-ui-react';
import 'semantic-ui-css/semantic.min.css';
import axios from 'axios';
import { getEnterpriseById } from "../../../Store/actions/EnterpriseAction";
import { connect } from 'react-redux'
class EnterpCards extends Component {
    state = {
        loading: this.props.enterp_loading,
        enterpId: null,
        permission: true,
        timeIsHoverError: "",
    }
    search = this.props.location.search;
    params = new URLSearchParams(this.search);
    key = this.params.get('key');
    componentDidMount() {
        axios.get(`http://localhost:64430/api/SendMail?key=${this.key}`).then(x => {
            this.setState({ enterpId: x.data, permission: true });
            this.props.selectEnterprise(this.state.enterpId);
        }).catch(x => this.setState({ timeIsHoverError: x.response.data.Message, permission: false }));
    }
    render() {
        return (
            <div>
                {this.props?<div>LOADING...</div>:
                this.state.permission ? <div>
                </div> : <Message size='massive' style={{textAlign:"center"}}> <Message.Header>Time Is Hover!</Message.Header>
                        <p>{this.state.timeIsHoverError}</p>
                    </Message>}
            </div>
        )
    }
}
const mapStateToProps = state => {
    return {
      enterp_loading: state.enterprise.loading,
    }
  }
const mapDispatchToProps = dispatch => {
    return {
        selectEnterprise: (id) => dispatch(getEnterpriseById(id)),
    }
}
export default connect(mapStateToProps, mapDispatchToProps)(EnterpCards);
