import React, { Component } from 'react'
import { Grid, Image, Header, Icon, Message, List, Segment } from "semantic-ui-react"
class lotery extends Component {
    state = {
        num1: null,
        num2: null,
        Addition: null,
        different: null,
        new: false,
        birthday: false,
        club_members: false,
        colum1: 16,
        colum2: 8,
        colum3: 8,
        enpty: false,
        loading: true
    }
    componentDidMount() {
        const x = this.props;
        x.Type === 1 ?
            this.setState({ different: x.Expiration, new: true })
            : x.Type === 2 ?
                this.setState({ different: null, birthday: true })
                :
                this.setState({ different: x.ExpireDate, club_members: true })
        x.LoteryType === "Discount" ?
            this.setState({ num1: x.Sum, num2: x.SumType + " Discount" })
            : x.LoteryType === "Plus Free" ?
                this.setState({ num1: x.Num1 + " + ", num2: x.Num2 + " Lottry" })
                :
                this.setState({ num1: x.Num1 + " in ", num2: x.Num2 + " $" })
        this.setState({ Addition: x.Addition })
    }
    render() {
        console.log(this.state)
        console.log(this.props)
        return <Segment style={{ height: "30%", width: "60vw", display: "inline-block", marginLeft: "5vw" }}>
            <List.Icon size="big" name={this.props.Type === 1 ? "user plus" : this.props.Type === 2 ? "birthday" : "credit card outline"}></List.Icon> {this.props.Type === 2 ? <div style={{ display: "inline-block", fontSize: "25px" }}>Congradulations</div> : ""}
            <List.Content style={{ fontSize: "17px", textAlign: "left" }}>
                <List.Header style={{ fontSize: "20px", display: "inline-block" }}>{this.props.Type === 1 ? "Lotery For Newer : " : this.props.Type === 2 ? "Lotery For Birthday : " : "Lotery For Club Members : "}</List.Header>
                  you have {this.state.num1}{this.state.num2}
            </List.Content>
        </Segment>
        // <div style={{display:"inline-block"}}>
        //     {this.props.Type === 3?<div style={{height:"40vh",width:"40%",border:"solid red",display:"inline-block"}}>club</div>:<div
        //     style={{marginLeft:"20%",display:"inline-block"}}>
        //         </div>}</div> 



        // <div style={{height: '10vh', textAlign: "center", width: "40vw", boxShadow: '5px 2px 1px -1px rgba(0, 0, 0, 0.2), 5px 5px 5px 5px rgba(0, 0, 0, 0.14), 5px 1px 7px 4px rgba(0, 0, 0, 0.12)',marginLeft:"18vw" }}>
        //     <div style={{ color: 'rgb(0, 181, 173)', borderBottom: 'none', fontSize: '120%' }}>{this.props.Type === 1 ? "Lotery For Newer:" : this.props.Type === 2 ? "Lotery For Birthday:" : "Lotery For Club Members:"}</div>
        //     <div>you have {this.state.num1}{this.state.num2}</div>
        //     <div>{this.state.Addition != null ? "*" + this.state.Addition : ""}</div>
        // </div>

    }
}
export default lotery;
