import isIsraeliIdValid from 'israeli-id-validator'
const validValue = (value, validation, id,text) => {
    let valid = true;
    console.log(validation);
    if (validation.mail) {
        if (!/^\w+([\.-]?\w+)*@\w+([\.-]?\w+)*(\.\w{2,3})+$/.test(value))
            return { id: id, error: text+" is not valid" };
    }
    if (validation.tz) {
        if (!isIsraeliIdValid(value))
            return { id: id, error: text+" is not valid" };
    }
    if (validation.password) {
        if (value.length < 6)
            return { id: id, error: text+" must have at least 6 characters" };
    }
    if (validation.phone) {
        if (value.length < 9)
            return { id: id, error: text+" is not valid" };
    }
    // if (tergets.minLength) {
    //     valid = value.length < tergets['maxLength'] && valid;
    // }
    return null;
}
export default validValue;
