        num1: null,
        num2: null,
        Addition: null,
        different: null,
        new: false,
        birthday: false,
        club_members: false,
        colum1: 16,
        colum2: 8,
        colum3: 8,
        enpty: false,
        loading:true
    }
    componentDidMount() {
        for (let x in this.props.lotery) {
            if (this.props.lotery.length === 0) {
                this.setState({ empty: true })
            }
            else{

            }
            x.Type === 1 ?
                this.setState({ different: x.Expiration, new: true })
                : x.Type === 2 ?
                    this.setState({ different: null, birthday: true })
                    :
                    this.setState({ different: x.ExpireDate, club_members: true })
            x.LoteryType === "Discount" ?
                this.setState({ num1: x.Sum, num2: x.SumType + " Discount" })
                : x.LoteryType === "Plus Free" ?
                    this.setState({ num1: x.Num1 + " + ", num2: x.Num2 + " Lottry" })
                    :
                    this.setState({ num1: x.Num1 + " in ", num2: x.Num2 + " $" })
            this.setState({ Addition: x.Addition })
        }
    }
    render() {
        console.log(this.state)
        console.log(this.props)
        return  <Segment style={{height:"30%",width:"60vw",display:"inline-block",marginLeft:"5vw"}}>
                <List.Icon size="big" name={this.props.Type === 1 ? "user plus" : this.props.Type === 2 ? "birthday" : "credit card outline"}></List.Icon> {this.props.Type === 2 ?<div style={{display:"inline-block",fontSize:"25px"}}>Congradulations</div>:""}
                <List.Content style={{fontSize:"17px",textAlign:"left"}}>
                    <List.Header style={{fontSize:"20px",display:"inline-block"}}>{this.props.Type === 1 ? "Lotery For Newer : " : this.props.Type === 2 ? "Lotery For Birthday : " : "Lotery For Club Members : "}</List.Header>
                  you have {this.state.num1}{this.state.num2}
                </List.Content>
           </Segment>
