import React, { Component } from 'react'
import 'semantic-ui-css/semantic.min.css';
import onChangeInput from "../Service/onChangeInput";
import onSubmit from "../Service/onSubmit";
import { Form, Grid, Icon, Label } from 'semantic-ui-react';
import axios from 'axios';
class ContactUs extends Component {
    state = {
        form: {
            Name: {
                text: "Name",
                value: "",
                maxLength: 40,
                validation: {
                    required: true,
                    pattern: /^/
                },
                error: "The name is incorrect",
                valid: false,
            },
            Email: {
                text: "E-mail address",
                value: "",
                maxLength: 50,
                validation: {
                    required: true,
                    pattern: /^/,
                    mail: true,
                },
                error: "The email is incorrect",
                valid: false,
            },
            Phone: {
                text: "Phone",
                value: "",
                maxLength: 10,
                validation: {
                    required: true,
                    pattern: /^[0-9]*$/,
                    phone: true,
                },
                error: "The phone is incorrect",
                valid: false,
            },
        },
        Description: "",
        trySend: false,
        loading: false,
        errorMessage: "",
    }
    onChangeHendler = (e, id) => {
        this.setState({ trySend: false, errorMessage: "" })
        const form = { ...this.state.form };
        const inputChanged = { ...form[id] };
        onChangeInput(e, inputChanged, form, id);
        this.setState({ form: form })
    }
    handleSubmit = (e) => {
        const form = { ...this.state.form };
        const obj = onSubmit(form)
        if (obj !== null) {
            this.setState({ form: form, trySend: true, errorMessage: obj.error });
        }
        else {
            this.setState({ loading: true });
            const data = {};
            for (let x in this.state.form) {
                data[x] = this.state.form[x].value;
            }
            data["Description"]=this.state.Description;
            console.log(data)
            axios.post(`http://localhost:64430/api/Contact`,data).then(x => {
                this.setState({ loading: false, errorMessage:x.data});
            }).catch(x => {
                this.setState({ loading: false, errorMessage: x.response.data.Message });
            });
        }
    }
    render() {
        const array = [];
        for (let x in this.state.form) {
            array.push({ id: x, config: this.state.form[x] })
        }
        return (<div style={{ backgroundColor: "#282c34" }}>
            <h1 style={{ textAlign: "left", borderBottom: "solid black", fontSize: "16px", paddingLeft: "1%" }}>Contact Us</h1>
            <Grid textAlign='center' celled='internally' verticalAlign='middle'>
                <Grid.Column width={11}>
                    <Form size='large' onSubmit={this.handleSubmit}>
                        <Form.Group>
                            {array.map((x, i) => {
                                return <Form.Input
                                    width={5}
                                    key={i}
                                    placeholder={x.config.text}
                                    maxLength={x.config.maxLength}
                                    type="text"
                                    onChange={event => this.onChangeHendler(event, x.id)}
                                    className={this.state.trySend && !this.state.form[x.id].valid ? "error" : x.id}
                                    value={this.state.form[x.id].value}>
                                </Form.Input>
                            })};
                        </Form.Group>
                        <Form.Group>
                            <Form.TextArea maxLength={500} value={this.state.description} onChange={event => this.setState({ description: event.target.value })} placeholder="Description" width={15}></Form.TextArea>
                        </Form.Group>
                        <div id="message" style={{ color: "red", textAlign: "left" }}>{this.state.errorMessage}</div>
                        <Form.Button width={14} type="submit" color='teal' >Contact Me</Form.Button>
                    </Form>
                </Grid.Column>
                <Grid.Column width={4} style={{ paddingBottom: "8%", paddingTop: "8%", textAlign: "left", width: 240, minWidth: 240 }}  >
                    <Label style={{ display: "block", padding: "3%", backgroundColor: "transparent", color: "white", fontSize: "100%", width: 240, minWidth: 240 }}><Icon name="envelope"></Icon>Email : easyclub100@gmail.com</Label>
                    <Label style={{ display: "block", padding: "3%", backgroundColor: "transparent", color: "white", fontSize: "100%", width: 240, minWidth: 240 }}><Icon name="fax"></Icon>Fax : 03-9258478</Label>
                    <Label style={{ display: "block", padding: "3%", backgroundColor: "transparent", color: "white", fontSize: "100%", width: 240, minWidth: 240 }}><Icon name="phone"></Icon>Phone : 052-7689412</Label>
                </Grid.Column>
            </Grid>
        </div>
        )
    }
}
export default ContactUs
