import React, { Component } from 'react';
import { getEnterprise } from '../../Store/actions/EnterpriseAction'
// import {getUser} from "../../Store/actions/UserAction"
import { Button, Icon, Form, Grid, Header, Message, Segment, Loader, Dimmer } from 'semantic-ui-react';
import 'semantic-ui-css/semantic.min.css';
import { NavLink } from 'react-router-dom';
import axios from 'axios';
import { connect } from 'react-redux'
import onChangeInput from "../Service/onChangeInput";
import onSubmit from "../Service/onSubmit";
import newPassword from "../Service/newPassword"
class Login extends Component {
  state = {
    form: {
      mail: {
        icon: "envelope",
        type: "text",
        value: "",
        text: "Email",
        validation: {
          pattern: /^[0-9a-zA-Z.+*%#!@]+$/,
          required: true,
          mail: true,
        }
      },
      password: {
        icon: "lock",
        type: "password",
        value: "",
        text: "Password",
        validation: {
          pattern: /^[0-9a-zA-Z]+$/,
          required: true,
          password: true,
        }
      }
    },
    trySend: "",
    loading: false,
    errorMessage: "",
    newPasswordMessage: "",
  }
  location = this.props.location.pathname;
  // componentDidUpdate() {
  //   console.log(this.props.user)
  //   if (this.props.user !== null)
  //     this.props.history.replace('cards')
  // }
  newPassword = () => {
    const form = { ...this.state.form };
    const obj = newPassword(form);
    if (obj !== null) {
      this.setState({ form: form, trySend: true, errorMessage: obj.error });
    }
    else {
      var route = "users";
      if (this.location !== '/users/login')
        route = "enterprises";
      this.setState({ loading: true })
      axios.get(`http://localhost:64430/api/${route}?email=${this.state.form.mail.value}`).then(x => {
        this.setState({ loading: false, newPasswordMessage: "sending email to:  " + this.state.form.mail.value });
      }).catch(x => {
        this.setState({ loading: false, newPasswordMessage: x.response.data.Message })
      });
    }
  }
  handleSubmit = (e) => {
    const form = { ...this.state.form };
    const obj = onSubmit(form)
    if (obj !== null) {
      this.setState({ form: form, trySend: true, errorMessage: obj.error });
    }
    else {
      if (this.location === '/users/login') {
        this.props.setLoading(true)
        axios.get(`http://localhost:64430/api/Users?password=${this.state.form.password.value}&email=${this.state.form.mail.value}`).then(x => {
          console.log(x.data)
        this.props.selectUser(x.data)
        if (this.props.user !== null)
           this.props.history.replace('cards')
          // this.props.setLoading(false)
          // this.props.history.replace('cards');
        }).catch(x => {
          this.props.setLoading(false)
          this.props.changeUserError(x.response.data.Message)
        })
      }
      else
        this.props.selectEnterprise(this.state.form.mail.value, this.state.form.password.value);
    }
  }
  onChangeHendler = (e, id) => {
    // this.state.changeUserError();
    // this.state.changeEnterpError();
    this.setState({ trySend: false, errorMessage: "" })
    const form = { ...this.state.form };
    const inputChanged = { ...form[id] };
    onChangeInput(e, inputChanged, form, id);
    this.setState({ form: form })
  }
  render() {
    const array = [];
    for (let x in this.state.form) {
      array.push({ id: x, config: this.state.form[x] })
    }
    return (
      <div>
        {this.props.message === null ? <Grid textAlign='center' style={{ height: '100vh' }} verticalAlign='middle'>
          <Grid.Column style={{ maxWidth: 450, boxShadow: '5px 2px 1px -1px rgba(0, 0, 0, 0.2), 5px 5px 5px 5px rgba(0, 0, 0, 0.14), 5px 1px 7px 4px rgba(0, 0, 0, 0.12)', borderRadius: '10%', height: '60%' }}>
            <Header as='h2' color='teal' textAlign='center' style={{ textShadow: "2px 2px 2px #888888,5px 5px 7px #888888" }}>
              Log in
            <br></br>
              <Icon.Group size='huge' style={{ marginTop: "1vh" }}>
                <Icon name='user circle' size='large' color='black' />
              </Icon.Group>
            </Header>
            <Form size='tiny' onSubmit={this.handleSubmit}>
              <Segment stacked>
                {array.map((x, i) => {
                  return <Form.Input
                    key={i} iconPosition="left"
                    type={x.config.type}
                    onChange={event => this.onChangeHendler(event, x.id)}
                    className={this.state.trySend && !this.state.form[x.id].valid ? "error" : x.id}
                    placeholder={x.config.text + "*"}
                    icon={x.config.icon}>
                  </Form.Input>
                })}
                <div id="message" style={{ color: "red" }}>{this.state.errorMessage}</div>
                <Button type="submit" color='teal' fluid size='large'>
                  Log in
            </Button>
                <div id="email_message" style={{ color: "red", marginTop: "1vh" }}>{this.props.userError || this.props.enterpError}</div>
              </Segment>
            </Form>
            <Message>
              New to us ?<NavLink to={this.location === '/users/login' ? '/users/signUp' : '/enterprises/signUp'} style={{ color: '#00b5ad' }}> Sign up</NavLink>
            </Message>
            <Message>
              <label >Forget your password ? <label onClick={this.newPassword} style={{ color: '#00b5ad', cursor: "pointer" }}> New password</label></label>
              <div id="newPassword" style={{ color: "red" }}>{this.state.newPasswordMessage}</div>
            </Message>
            <Dimmer id='dimmer' active={this.props.enterp_loading !== false || this.props.user_loading} inverted='false'>
              <Loader size='large'>Loading</Loader>
            </Dimmer>
          </Grid.Column>
        </Grid> :
          <Message size='massive' style={{ textAlign: "center", position: "absolute", top: "30%", width: "80%", marginLeft: "10%" }} >
            <Message.Header>Your request has been received successfully</Message.Header>
            <p>Sending link for your account to your email ,the link will be valid for the next 15 minutes</p>
          </Message>}
      </div>
    )
  }
}
const mapStateToProps = state => {
  return {
    user: state.user.user,
    enterprise: state.enterprise.enterprise,
    userError: state.user.error,
    enterpError: state.enterprise.error,
    user_loading: state.user.loading,
    enterp_loading: state.enterprise.loading,
    message: state.enterprise.message
  }
}
const mapDispatchToProps = dispatch => {
  return {
    selectEnterprise: (mail, password) => dispatch(getEnterprise(mail, password)),
    selectUser:(user) => dispatch({ type: "SELECT_USER", payload: user }),
    setLoading: (status) => dispatch({ type: "LOADING", payload: status }),
    changeUserError: (error) => dispatch({ type: "ERROR", payload: error }),
    // changeEnterpError: () => dispatch({ type: "ERROR", payload: "" })
  }
}
export default connect(mapStateToProps, mapDispatchToProps)(Login);
